INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("- 20.000", "", "Os d’Ishango", "", "Os d'Ishango (Congo) daté de 20.000 ans avant notre ère, présentant de nombreuses entailles, groupées de façon systématique : il a servi de support de comptage et de mémorisation. (cf ;Institut Royal des Sciences naturelles de Belgique)}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("-4e millénaire", "", "", "Elam et Sumer (Mésopotamie)", "Apparition de précurseurs de l’écriture chez les sumériens. Ce sont des boules d'argile remplies de cailloux d’argile permettant de garder une trace de comptes. (En latin, caillou se dit calculus d'où vient l’expression « avoir un calcul aux reins !! »). EXPOT : reproduction de bulles avec des calculi (à faire) ou photo ?}", "Ca", "-40000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("-3.300 env", "", "", "Basse Mésopotamie", "Apparition de l'écriture pré-cunéiforme sur tablettes d’argile. Cette écriture primitive (dépourvue de grammaire) implique une numération primitive et est destinée uniquement à garder une trace d’échanges commerciaux,en Basse Mésopotamie. EXPOT : reproduction de tablette ? (à faire) ou photo ? Tablette (fac-similé) + tablette tactile « de la tablette d'argile à la tablette tactile »}", "Ec", "-34000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("-2.900 env.", "", "", "Mésopotamie", "Apparition de l’écriture cunéiforme qui est destinée à accumuler tout savoir (sur tablettes d’argile)..}", "Ca", "-33000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("-70 environ", "", "Machine d'Anticythère", "Grèce", "Fabrication, en Grèce, de la machine (ou mécanisme) d'Anticythère, qui est considérée comme le premier calculateur analogique antique permettant de calculer des positions astronomiques. C'est un mécanisme de bronze comprenant trois dizaines de roues dentées, solidaires et disposées sur plusieurs plans. Il est garni de nombreuses inscriptions grecques.}", "Ca", "-870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("Période romaine", "", "Boulier compteur", "", "Utilisation par les Romains de bouliers (compteurs) (abaques dont les boules ou jetons sont fixés et ne peuvent être déplacés que sur un axe linéaire) en métal, de la taille d’une main. Il est malheureusement impossible de dater ces instruments de calcul. Il est vraisemblables que ces bouliers se répandirent à partir de Rome vers la Chine et ensuite au Japon et en Russie.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1er siècle", "", "Premier livre à pages", "Rome", "Invention, à Rome, du codex : premier livre à pages, réalisées en parchemin (peau de bête) (.}", "C", "0");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2e siècle", "", "Papier", "Chine", "Invention du papier par les Chinois.}", "IO", "1050000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("3e siècle", "", "", "", "’Invention d’un système de renvoi entre ? documents (en l’occurrence les 4 évangiles) : les canons de concordances (pouvant être considérés comme les ancêtres des hyperliens).}", "C", "2000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("5e siècle", "", "", "", "Invention en Inde de la numération de position (la valeur d’un chiffre dépendant de sa position dans le nombre) : base de l’arithmétique moderne et support du fonctionnement des futures machines à calculer.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("476", "", "", "", "Chute de l’Empire romain.}", "H", "4760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("Fin du 8e siècle", "", "", "", "Introduction de la numération positionnelle indienne chez les Arabes. Désormai l’appellation de ‘chiffres arabes’ va prévaloir. La notation arabe est la seule dans laquelle les chiffres peuvent être utilisés de façon agréable et efficace pour réaliser des calculs écrits.}", "Ca", "7000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("711", "", "", "Espagne", "Conquête de l’Espagne par les musulmans.}", "H", "7110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("800", "", "", "Rome", "Charlemagne (742-814) est sacré empereur d’Occident, le 25 décembre 800.}", "H", "8000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("820 env", "", "", "Ouzbékistan", "Abdallah Muh ibn Musa Al Khwarizmi (790-850), un mathématicien originaire de l’actuel Ouzbékistan, rédige un procédé de calcul. Actuellement, ’on nomme « Algorithme » (mot dérivé d’Al Khwarizmi) un raisonnement logique.}", "Ca", "8200000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("10e siècle", "", "", "Espagne", "Introduction de la numération de position en Europe par l’entremise des Arabes.}", "Ca", "8000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("12e au 15e siècle", "", "", "Eur", "En Europe occidentale, la graphie des chiffres « arabes » se stabilise.}", "Ca", "11000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("13e siècle", "", "", "", "Apparition des bouliers compteurs chinois au plus tard au 13e siècle. (Un texte datant de cette époque explique leurs règles de fonctionnement). EXPOT : boulier compteur chinois (SYSCAL.68.1201) et boulier japonais – soroban (SYSCAL.68.1206)}", "Ca", "12000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1425", "", "", "Be", "Création d’une université à Louvain.}", "H", "14250000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("milieu du 15e siècle", "", "", "Emp Rom Germanique", "Gutenberg (1400-1468) développe un procédé d’impression à caractères mobiles et imprime la Bible. Ce processus a accéléré et multiplié la diffusion des livres et, dès lors, aidé à la propagation des connaissances. EXPOT : casses et meuble de casses d’imprimerie (I&B), liste des caractères mobiles utilisés par Gutenberg ; page de la premières Bible imprimée par Gutenberg, histoire de la typographies (tableau)}", "H", "14520000[a]");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1492", "", "", "Amérique/Espagne", "Christophe Colomb (1451-1506) découvre l’Amérique.}", "H", "14920000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1509", "", "", "Pays-Bas", "Érasme (1467-1536), une des figures majeures de la culture européenne, écrit son ouvrage L’éloge de la folie.}", "H", "15090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1519", "", "", "Pays-Bas", "Charles Quint (1500-1558) est couronné empereur Romain, il sera le monarque chrétien le plus puissant de la première moitié du 16e siècle.}", "H", "15190000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1530", "", "", "Prusse", "Copernic (1473-1543) publie son œuvre majeure sur l’héliocentrisme : De Revolutionibus Orbium Coelestium (Révolutions de sphères célestes).}", "H", "15300000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1530 env.", "", "", "", "Réalisation, dans la région actuellement nommée Belgique et Pays-Bas, de cylindres de programmation à picots mobiles, permettant de changer facilement l’air joué de façon automatique par un carillon. Ce dernier est donc programmable et probablement le premier à présenter cette caractéristique dans l’histoire des techniques.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("17e siècle", "", "", "", "C’est seulement au 17e siècle que le boulier japonais (‘soroban’) connaît une utilisation populaire. C’est aussi seulement à cette époque qu’en Europe, les chiffres arabes gagnent du terrain puis supplantent non seulement les chiffres romains mais aussi l’usage des abaques.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1617", "", "Logarithmes", "Ecosse", "Le mathématicien écossais, John Napier (ou Neper) (1550-1617), qui avait inventé les logarithmes, invente un procédé de calcul facilitant le calcul des produits, quotients, puissances et racines, appelé bâtons de Napier.}", "Ca", "16170000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1628", "", "", "UK", "William Oughtred, un mathématicien anglais, invente la règle à calcul (instrument mécanique de calcul analogique) linéaire ou circulaire. Elle sera encore utilisée au 20e siècle.}", "Ca", "16280000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1642", "", "", "Fr", "En 1642, Le Français Blaise Pascal (1623-1662), qui sera philosophe, écrivain, mathématicien et physicien, réalise la première Pascaline (machine à calculer mécanique réalisant des additions et des soustractions (par la méthode des compléments arithmétiques)) alors qu'il n'a que 19 ans. C'est la plus ancienne machine à calculer qui soit encore conservée actuellement.}", "Ca", "16420000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1643", "", "", "", "Début du règne de Louis XIV (1638-1715), surnommé le Roi-Soleil.}", "H", "16430000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1673", "", "", "", "Construction de la première machine multiplicatrice par Gottfried Wilhelm Leibniz. Ce dernier, né à Leipzig, était à la fois jurisconsulte, philosophe, et mathématicien mais aussi linguiste, historien, géographe, diplomate, théologien.}", "Ca", "16860000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1725", "", "", "", "Basile Bouchon invente en France un support à trous (bande de papier perforée) pour programmer un métier à tisser.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1738", "", "", "FR", "Jacques Vaucanson présente à Paris ses automates ludiques: le Joueur de flûte et le Canard digérateur.}", "HW", "17380000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1745", "", "Condensateur", "DE", "Pieter van Musschenbroek (De) invente le condensateur appelé alors « Bouteille de Leyde ».}", "E", "17450000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1752", "", "Paratonnerre", "USA", "Benjamin Franklin invente le paratonnerre.}", "E", "17520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1770 envir.", "", "", "FR", "Les frères Jaquet-Droz, des horlogers suisses, finissent la construction du Dessinateur, de la Musicienne et de l'Écrivain : des automates mécaniques respectivement capables de réaliser 4 dessins, de jouer du piano et d'écrire des missives (de 40 lettres maximum). Il est possible de choisir le texte à faire écrire par l'écrivain : ce dernier est donc programmable.}", "HW", "17740000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1789", "", "Révolution française", "Fr", "Révolution française et Déclaration des Droits de l'Homme et du Citoyen en France.}", "H", "17890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1789", "", "", "USA", "George Washington (1732-1799) devient le premier président des USA.}", "H", "17890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1794", "", "", "Fr", "Claude Chappe (1763-1805) invente en France un télégraphe optique :un appareil que l'on utilise pour transmettre des informations par signaux visuels, par temps clair.}", "C", "17940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1799", "", "", "It", "Alessendro Volta (1745-1827) crée un appareil capable d'accumuler de l'électricité : la pile de Volta. On appellera ensuite « volt » la mesure de la tension électrique. Son invention permet au chimiste britannique John Daniell de créé la pile Daniell en 1836 qui est très proche de nos piles modernes.}", "E", "17990000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1801", "", "", "Fr", "Joseph Marie Jacquard (1752-1834) perfectionne, à Lyon, la programmation des métiers à tisser et recourt à des cartes perforées qui sont reliées entre elles pour former une bande et dont la succession constitue un programme donné.. Expôt : bandes Jacquard (I&B)}", "IO", "18040000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1815", "", "", "Be", "Bataille de Waterloo le 18 juin 1815.}", "H", "18150618");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1817", "", "", "", "Création de l'Université de Liège.}", "H", "18170000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1817", "", "", "", "Création de l'Université de Gand.}", "H", "18170000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1820", "", "", "", "Début en France de la « révolution industrielle ».}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1820", "", "Arithmomètre", "FR", "Charles Xavier Thomas (de Colmar) (1785-1870) met au point l' « Arithmomètre », première multiplicatrice à être largement commercialisée : 1.500 exemplaires seront vendus entre 1821 et 1878.}", "Ca", "18220000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1822", "", "Machine à différence", "", "L’anglais, Charles Babbage (1791-1871), commence le développement de la Machine à différence (« Difference Engine »).}", "Ca", "18320000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1830", "", "Indépendance de la Belgique", "Be", "Indépendance de la Belgique, le 4 octobre 1830.}", "H", "18301004");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1831", "", "Relais électromécanique", "", "L’Américain Joseph Henry invente le relais électromécanique. Ces types de relais seront utilisésdans la télégraphie électrique au 19e siècle puis dans des machines à calculer électromécanques des années 1940..}", "E", "18310000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1831", "", "", "BE", "Léopold Ier (1790-1865) de Belgique est le premier roi des Belges.}", "H", "18310000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1834", "", "Fondation de l’ULB", "", "Création de l'Université Libre de Bruxelles.}", "H", "18340000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1834", "", "Machine analytique", "UK", "Charles Babbage (1791-1871) commence, à Londres, le développement de la Machine analytique (« Analytical Engine ») la première machine à calculer programmable, complètement mécanique. Le programme était constitué d’une suite de cartes perforées, inspirées par celles d’un métier à tisser Jacquard : une carte perforée correspondait à une instruction. La machine devait être capable de traiter l’instruction : « si …, alors… » (appelée par les informaticiens le branchement conditionnel. Elle était constituée d’un « moulin » afin de réaliser les calculs, d’un « magasin » pour stocker les données et des organes d’entrée et de ceux de sorties. Elle devait offrir aussi une imprimante. Cette machine, bien que restée inachevée, est reconnue comme un ancêtre des ordinateurs.}", "Ca", "18340000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("18371838", "", "Code Morse", "USA", "L'américain, Samuel Morse (1791-1872), dépose un brevet de télégraphe électrique en 1837. L’année suivante, il propose un codage ingénieux de l’alphabet et des chiffres, composé de deux éléments (signal long et signal court) .}", "C", "18350000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1843", "", "Premier programme", "UK", "Ada Lovelace (1815-1852), à la suggestion et avec la collaboration de Charles Babbage, annote un texte écrit sur la Machine analytique. Une de ses annotations propose un programme de calcul Ada Lovelace est dès lors considérée par certains comme la première programmeuse de l’Histoire.}", "Ca", "18430000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1847", "Siemens", "", "DE", "Werner Siemens (18196-1892) crée l'entreprise Telegraphen Bau-Anstalt von Siemens & Halske le 1er octobre 1847 en Allemagne.}", "Ent", "18471001");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1847", "", "Algèbre de Boole", "UK", "L'anglais, George Boole (1815-1864) écrit L'analyse mathématique de la Logique où il définit les opérations de l’algèbre logique binaire [et, ou, non].}", "Ca", "18479000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1859", "", "", "Fr", "Le Français, Gaston Planté (1834-1885) invente la première batterie électrique rechargeable.}", "E", "18590000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1860", "", "", "USA", "Abraham Lincoln (1809-1865) devient président des USA et le restera jusqu’en 1868.}", "H", "18600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1865", "", "", "BE", "Léopold II de Belgique (1835-1909) devient le deuxième roi des Belges.}", "H", "18650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1866", "", "", "", "Après une dizaine d’années d’effort, mise en place définitivement fructueuse, sous la direction de l’Anglais Lord Kelvin, d'un câble transatlantique (entre Terre-Neuve et l'Irlande) pour le télégraphe électrique .}", "C", "18660000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1867", "", "", "USA", "Mise au point par les américains Christopher Latham Sholes, Samulet W. Soule et Carlos Glidden de la machine à écrire : Literary Piano. Elle sera produite en série par Remington sous le nom de Remington Model I dès 1873.}", "C", "18670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1870", "Burroughs", "", "USA", "L'américain William Burroughs (1857-1898) réalise et commercialise la première machine à calculer avec impression des résultats. EXPOT}", "C", "18700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1870", "", "", "FR", "Jules Verne publie son roman Vingt mille lieues sous les mers.}", "H", "18700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1871", "", "Dynamo", "BE", "La dynamo du Belge Zénobe Gramme (1826-1901) voit le jour dont le brevet français numéro 87.938 avait été déposé en 1869. C'est un générateur de courant électrique qui pourra fonctionner comme moteur.}", "E", "18710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1874", "", "Code Baudot", "Fr", "Le Français Émile Baudot (1845-1903) développe le code Baudot pour les télégraphes à 5 touches. C'est l'origine de la mesure en baud (nombre de bits par seconde).}", "C", "18740000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1876-1877", "Bell", "Téléphone", "USA", "Graham Bell (1843-1922) obtient, aux USA, un brevet pour son invention, le téléphone,. L’année suivante, il fonde la compagnie Bell (qui deviendra American Telephone and Telegraph Company (AT&T) en 1899).}", "C", "18760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1877", "", "", "", "EXPOT : SYSCAL. 68.1060.règle à calcul (instrument analogique) spécialisée Courroies J. Lechat {fabricant de courroies en coton à Lille et à Gand, entre 1877 et 1930}}", "Ca", "18770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1877", "", "", "FR/USA", "Aux USA, Thomas A. Edison (1847-1931) revendique la paternité d'un dispositif d'enregistrement de sons sur cylindres de cire. Il deviendra quelques mois plus tard le \"père\" du phonographe. Il mettra également au point la lampe à incandescence en 1879.}", "S", "18770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1882", "Burroughs", "", "USA", "L'américain William Seward Burroughs (1857-1898) construit en 1882 une des premières additionneuses imprimante à touches. Quatre ans plus tard, il fonde la compagnie Burrouhgs.}", "Ca", "18850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1882", "Bell", "Fondation de Bell", "BE", "Création de la Bell Telephone Manufacturing Company à Anvers le 26 avril 1882.}", "C", "18820426");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1882", "", "", "", "Fondation de la NCR (National Cash Register Company) à Dayton (USA) par John Patterson. La société sera connue pour ses caisses enregistreuses. Elle pratiqua une politique commerciale très agressive et créa une culture d’entreprise.}", "", "18840000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1884", "IBM", "", "USA", "Herman Hollerith (1860-1929), né à Buffalo (USA), dépose en 1884 un première demande de brevet pour sa Machine mécanographique/tabulatrice électrique. Cette machine, initialement prévue pour fonctionner avec un ruban perforé fut révisée pour employer des cartes perforées. Ces dernières contiennent uniquement des données.et sont utilisées une par une, en succession, pour incrémenter des compteurs en fonction de la position des trous qu’elles comportent… Le gouvernement américain utilisera cette machine à statistiques pour le recensement de 1890. expot}", "HW", "18870608");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1885", "", "Prototype du comptometer", "USA", "L'américain Dorr E. Felt construit le prototype expériementaldu « Comptometer », surnommé « Macaroni Box » car il est composé de matériaux hétéroclites tels que du bois, des élastiques, des épingles à cheveux,... Cette invention est brevetée en 1887. Le Comptometer est la première machine à additionneuse à grand clavier complet (multitouche) qui est construit de façon industrielle.}", "Ca", "18850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1885", "", "", "BE", "Création de la première « centrale électrique » en Belgique pour l'éclairage de la rue Neuve à Bruxelles.}", "E", "18850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1896", "IBM", "", "USA", "Herman Hollerith fonde en 1896 la Tabulating Machine Company (TM) qu’il vendra en 1911. TM deviendra d’abord la Computing Tabulating and Recording Co (CTR) puis, finalement, en 1924, l’International Business Machines (IBM) sous la direction de Thomas Watson.}", "Ent", "18900000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1887", "", "", "De", "L'Allemand, Heinrich Hertz (1857-1894) découvre les ondes hertziennes, menant à la téléphonie sans fil.}", "E", "18870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1895", "", "Fondation de l’OIB", "BE", "Les Belges, Paul Otlet (1868-1944) et Henri La Fontaine (1854-1943) créent à Bruxelles l'Office International de Bibliographie (OIB). Ce dernier deviendra, plus tard, le Mundaneum, aujourd'hui installé à Mons. Paul Otlet va développer, avec Henri La Fontaine, la Classification Décimale Universelle (CDU), encore utilisée de nos jours dans les inventaires des bibliothèques et les catalogues.}", "H", "18950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1897", "ACEC", "", "BE", "Première ligne de tramways électrifiée à la côte Belge créée par Électricité et Hydraulique à Charleroi (E&H) qui deviendra les Ateliers de Construction Électrique de Charleroi (ACEC) en 1904.}", "E", "18970000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1899", "", "", "", "Exposition Universelle de Paris où Herman Hollerith présente sa machine à statistiques.}", "", "18990000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1901", "", "", "", "Guilierlmo Marconi (1874-1937) réalise les premières liaisons transatlantiques en radiotélégraphies à grande distance, entre Poldhu (Cornouailles) et Terre-Neuve (Canada) le 12 décembre 1901.}", "C", "19011212");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1904", "", "", "", "EXPOT : Underwood clavier alphabétique et numérique, motorisé, provenance connue (I&B) + machine à écrire Underwood des années 1984.}", "Ca", "19040000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1904", "", "Diode à vide", "UK", "John Ambrose Fleming (UK), conseiller scientifique à la société Marconi, invente, en juin 1904, la diode à vide qui permet de créer un courant unidirectionnel à partir d'un courant alternatif.}", "E", "19040600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1905", "Burroughs", "", "USA", "American Arithmometer Company devient Burroughs Adding Machine Company (USA).}", "Ent", "19050000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1905", "", "", "", "Publication de la première édition complète de la Classification Décimale Universelle (CDU), développée par Paul Otlet et Henry La Fontaine.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1907", "", "", "USA", "L'américain Lee De Forest (1873-1961) invente la première triode, (tube à vide capable de provoquer l'amplification d'un signal électrique).}", "E", "19070000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1908", "", "", "BE", "Loi belge sur la télégraphie et téléphonie sans fil par les radiations électriques le 10 juillet 1908.}", "E", "19080710");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1909", "", "", "BE", "Albert Ier (1875-1934) de Belgique devient le troisième roi des Belges.}", "H", "19090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1910", "", "", "", "Exposition universelle de Bruxelles et création du Musée international « En marge de l’Exposition universelle de Bruxelles et dans le cadre du Congrès mondial des Associations internationales, une exposition musée est organisée pour permettre à chaque association internationale de présenter ses activités. Ce musée international sera ensuite installé au Palais du Cinquantenaire : il réunira des collections internationales visant à illustrer le monde et ses connaissances. » (cf. site du Mundaneum)}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1911", "", "", "", "L'américain, James Powers fonde son entreprise : l'Accounting Machines Company (AMC).}", "", "19110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1912", "", "Naufrage du Titanic", "", "Naufrage du paquebot « Titanic » le 15 avril 1912.}", "H", "19120415");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1914", "Powers", "", "USA", "Les tabulatrices de la Powers Accounting Machine Company sont dotées d'une imprimante numérique (qui n'imprime que des chiffres). Hollerith suivra en 1921.}", "C", "19140000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1914", "", "Début WW1", "", "Début de la Première Guerre Mondiale le 28 juin 1914.}", "H", "19140628");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1915", "MBLE", "", "BE", "La Société Lampes Brabant s'installe à Anderlecht et devient la Manufacture Belge de Lampes Électriques (M.B.L.E.).}", "Ent", "19150000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1916", "", "", "", "Albert Einstein (1879-1955) publie La théorie de la relativité restreinte et générale.}", "H", "19160000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1918", "", "", "DE", "Création à des fins commerciales d’une machine à crypter à base de rotors appelée « Enigma », par Arthur Scherbius (DE). Cette machine est produite en série (> 30 000 exemplaires) avec de modifications (nouveaux câblages + divers raffinements techniques), par Scherbius, à partir de 1925 pour le gouvernement et les militaires allemands. Elle servira aux Allemands pendant la guerre de 1940-1945.}", "C", "19180000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1918", "", "Fin de WW1", "", "Fin de la Première Guerre Mondiale le 11 novembre 1918.}", "H", "19181111");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1920", "", "", "Be", "Les femmes belges obtiennent le droit de vote, le 15 avril 1920.}", "H", "19200415");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1920", "", "", "", "« En 1920, le Musée international et les institutions créées par Paul Otlet et Henri La Fontaine occupent une centaine de salles. L’ensemble sera désormais appelé Palais Mondial ou Mundaneum. »}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1921", "Burroughs", "", "USA", "Burroughs achète la Moon-Hopkins facturation Machine Company du Missouri, et la machine Moon-Hopkins est redessinée et améliorée. EXPOT}", "Ent", "19210000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1921", "", "Mot robot", "Tch", "Invention du mot robot par Karel Capek, auteur dramatique tchèque, dans une de ses pièces.}", "H", "19210000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1921", "Bull", "", "No", "Le 23 décembre 1921, le norvégien, Fredrik Rosing Bull introduit une demande de brevet pour une machine automatique d'enregistrement pour statistiques et opérations similaires et la fabrique. Il crée en même temps la société Bull.}", "HW", "19211223");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1922", "Bull", "", "No", "Création de la trieuse verticale de cartes perforées Bull. Plus tard, les trieuses seront horizontales afin de facilité le travail de l'opérateur.}", "HW", "19220000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1924", "IBM", "", "USA", "Le 14 février 1924, la CTR (Computing Tabulating and recording) Co fut renommé the International Business Machines Corporation (IBM) avec comme président Thomas J. Watson.}", "Ent", "19240214");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1925", "Burroughs", "", "USA", "Burroughs lance la première machine portable à additionner.}", "Ca", "19250000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1925", "Burroughs", "", "BE", "La Société Anonyme Burroughs (filiale de Burroughs USA) est créée à Bruxelles.}", "Ent", "19250000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1925", "Bull", "", "No", "Décès du norvégien, Fredrik Rosing Bull, le 7 juin 1925. R.A. Knutsen prend le relais pour le développement des machines et de la société Bull.}", "Ent", "19250607");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1927", "Bell", "", "USA", "Première émission de télévision entre New York et Washington (USA) par la Bell Telephone.}", "C", "19270000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1927", "Remington Rand", "", "USA", "Remington-Rand achète diverses compagnies dont la Powers Accounting Machine Company.}", "Ent", "19270000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1928", "Bull", "", "Sui", "La société suisse H.W. Egli commercialise des machines sous un brevet Bull.}", "Ent", "19280000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1928", "FNRS", "", "BE", "Création du Fonds National de la Recherche Scientifique (FNRS) qui a pour mission de développer la rechercher scientifique fondamentale.}", "H", "19280000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1928", "IBM", "Carte perforée IBM 80 col.", "USA", "IBM (International Business Machines) lance sa carte perforée 80 colonnes à trous rectangulaires et un code alphanumérique associé, dit \"code Hollerith\" (code « cartes »). La carte sera brevetée dans le monde entier. IBM intentera divers procès contre Bull pour contrefaçon.}", "S", "19280000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1930", "INR", "", "BE", "Création de l'Institut National de Radiodiffusion (INR) à Bruxelles le 18 juin 1930.}", "C", "19300618");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1930", "RTT", "", "BE", "Fondation de la Régie des Télégraphes et Téléphones (RTT), en Belgique, le 19 juillet 1930.}", "Ent", "19300719");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1931", "Bull", "", "Fr", "La fabrication des machines Bull est transférée à Paris (92bis rue Gambetta), la société Egli-Bull s'y installe.}", "Ent", "19310000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1932", "", "", "USA", "Création de la première calculatrice IBM (International Business Machines), la 601, qui peut lire 2 nombres dans une carte et en perforer l'addition ou la multiplication dans une troisième zone de la même carte.}", "Ca/S", "19320000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1932", "", "", "", "Invention par Gustav Tauschef (Autriche) des mémoires tambours, c'est la première mémoire « gros volume » fiable, les tambours seront remplacés par les mémoires à tores de ferrite et puis par des disques durs. Expot}", "", "19320000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1932", "Bull", "", "FR", "Les papeteries Aussedat (Fr.) entrent au capital d'Egli-Bull. C'est la première papeterie européenne à fabriquer des cartes perforées. Les cartes perforées doivent respecter de nombreuses exigences.}", "Ent", "19320000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1932", "Bull", "", "", "La tabulatrice à cylindres alphanumériques Bull de K.A. Knutsen est commercialisée.}", "", "19320000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1933", "", "", "USA", "Franklin D. Roosevelt (1882-1945) devient président des USA (1933-1945).}", "H", "19330000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1933", "Bull", "", "Fr", "Fabrication de la tabulatrice T30 connectée à une imprimante cylindrique créée par Knutsen qui va jusqu'à 135l/min. Un record pour l'époque ! Elle sera fabriquée et commercialisée en France à partir de 1934.}", "IO", "19330000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1933 (ou 32 selon Marguin", "BullCMB", "", "", "Changement de nom pour la compagnie ‘H.W. Egli-Bull’, le 31 mars 1933, : elle devient la « Compagnie des Machines Bull » (CMB). Les capitaux en sont répartis comme suit : H.W. Egli-Bull : 25% , et l’État français 75%.}", "Ent", "19330300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1934", "BARCO", "", "BE", "Création de la Belgian American Radio Corporation (BARCO) par Lucien De Puydt (1903-1940).}", "Ent", "19340000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1934", "", "", "BE", "Le belge Paul Otlet écrit son « Traité de documentation ». Il y développe l'idée d’une organisation mondialedes connaissances .}", "H", "19340000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1934", "", "", "BE", "Léopold III de Belgique (1901-1983) devient le quatrième roi des Belges.}", "H", "19340000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1936", "", "", "", "Le mathématicien anglais Alan Turing (1912-1954) publie un article de mathématiques théoriques, qui se révélera historique, intitulé ‘Sur les nombres calculables, avec une application au problème de la décision’. Il y développe son concept de machine machine idéale (/ abstraite [sans limitation de temps ni d’espace] (qu'il nomme 'machine universelle' et sera appelée ultérieurement 'machine de Turing') pour exécuter tout algorithme. Il y démontre qu’il existe des problèmes qui ne peuvent PAS être résolus par un algorithme.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1936", "", "", "", "Sortie du film Les temps modernes de Charlie Chaplin (1889-1977), comédie critiquant le monde industrialisé et mécanisé.}", "H", "19360000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1937", "", "", "USA", "Création du tout premier additionneur binaire par Georges Robert Stibitz dans sa cuisine.}", "Ca", "19370000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1937", "Bull", "Tabulatrice Bull BS120", "USA", "La Compagnie des Machines Bull commercialise sa tabulatrice BS120, dont l’mprimante peut atteindre 150 lignes par minute.}", "Ent", "19370000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1938", "Zuse", "", "De", "L’ingénieur civil Konrad Zuse (1910-1995), termine, en Allemagne, dans l’appartement de ses parents, son premier prototype de calculateur binaire, avec programme externe: le Z1 : cette machine est complètement mécanique et se révèle peu fiable. Expôt : cadre Z1 + tableau de Zuse}", "Ca", "19380000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1939", "", "ABC", "USA", "Les américains John Vincent Atanasoff et Clifford Berry conçoivent l'Atanasoff–Berry Computer (ABC) : le premier calculateur électronique.}", "Ca", "19390000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1939", "Bell Labs", "", "USA", "George Stibitz (USA) crée le Bell Telephone Laboratories (BTL) Model I, un calculateur électromécanique non programmable, capable de manipuler des nombres complexes. Appelé également « Complex Number Computer », cette machine a permis en septembre 1940 d'envoyer, via un télétype, la réponse d'une opération en moins d'une minute du New-Hampshire à New-York. C'est la naissance du travail à distance et de la télématique.}", "HW", "19390000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1939", "HP", "Fondation de HP", "USA", "Hewlett-Packard est fondé, à Palo Alto (USA), par William Hewlett et David Packard le 1er janvier 1939.}", "Ent", "19390101");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1939", "", "Début WW2", "", "Début de la Seconde Guerre Mondiale le 3 septembre 1939.}", "H", "19390903");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1939", "", "", "", "A partir de 1939, Alan Turing renonce provisoirement à sa carrière universitaire pour se mettre au service du gouvernement britannique et entre à Bletchley Park pour collaborer à la recherche de la clé du système de cryptage Enigma des Allemands.}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1941", "", "", "", "EXPOT : traité de mécanographie comptable SYSCAL 68.1202.}", "", "19410000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1941", "Zuse", "Z3", "DE", "Konrad Zuse (De) achève, la machine qui sera appelé Z3, partiellement financé par le gouvernement allemand. La Z3 sera présentée à quelques scientifiques allemands le 12 mai 1941. Sa mémoire et son processeur sont électromécaniques (relais). La Z3 travaille en virgule flottante (ce qui permet une grande précision dans les calculs avec des nombres très petits ou très grands). Elle est la première réalisation d’un calculateur universel contrôlé par un programme (externe) fonctionnant correctement mais elle est restée très longtemps inconnue de la communauté scientifique internationale. à)}", "Ca", "19410512");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1941", "", "Bombe de Bletchley Park", "UK", "Fin de la création, à Bletchley Park (UK), de la première Bombe, basé en partie sur la conception de la polonaise Bomba, un moyen électro-mécanique de décrypter les communications militaires codées par la machine Enigma des armées allemandes pendant la Seconde Guerre mondiale. La méthodologie de décryptage est de Alan Turing avec une forte contribution de Gordon Welchman.}", "HW", "19410000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1942", "", "", "", "John William Mauchly (1907-1980) rédige une note The Use of High Speed Vacuum Tube Devices for Calculating dans lequel il utilise pour la première fois le verbe « to program » dans le sens actuel.}", "H", "19420000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1942", "Bull", "", "", "La tabulatrice BS de Bull est perfectionnée : ses cylindres de fonctionnement ne sont plus fixes mais programmables.}", "HW", "19420000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1943", "IBM", "", "USA", "Harvard MARK I, le plus grand calculateur électro-mécanique, est livré à l'Université de Harvard (IBM), conçu par H. H. Aiken aidé par une équipe et des finances d'IBM (International Business Machines).}", "E", "19430000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1943", "", "", "UK", "Une équipe dirigée par Thomas Flowers est installée près de Londres, à Bletchley Park, et crée l'ordinateur Colossus selon les exigences formulées par Max Newman. Il était utilisé pendant la Seconde Guerre mondiale pour la cryptanalyse du code Lorenz. Il sera opérationnel à partir de décembre 1943. EXPOT : La guerre du chiffre par radios et calculateurs électroniques interposés, à la fin de la Seconde Guerre mondiale. Tableau adapté par l'auteur d’après R. Ligonniere ; dans Histoire universelle des chiffres Georges Ifrah T II, p. 579.}", "Ca", "19431200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1944", "IRSIA", "", "BE", "Création, en Belgique, de l'Institut pour l'encouragement des Recherches Scientifiques, l'Industrie et l'Agriculture (IRSIA) le 27 décembre 1944, fermeture en 1994.}", "H", "19441227");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945", "", "", "USA", "L'américain Vannevar Bush (1890-1974) invente le concept de l’hypertexte, un système permettant de se déplacer d'un point d'un document à un autre point dans le même document ou dans un autre document, il l'appel memex.}", "C", "19450000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945", "", "", "USA", "Harry S. Truman (1884-1972) devient président des USA (1945-1953).}", "H", "19450000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945", "", "Architecture de von Neumann", "Hongu", "John von Neumann (1903-1957), John Presper Eckert (1919-1995) et John William Mauchly (1907-1980) (et d'autres) définissent un type d'architecture d’ordinateur dans First Draft of a Report on the EDVAC, le 30 juin 1945. On l'appelle « Architecture de von Neumann » car il était le rapporteur des travaux pionniers en la matière. Cette architecture est utilisée dans la quasi-totalité des ordinateurs modernes. EXPOT : photo EDVAC}", "T", "19450630");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945", "", "", "", "Premier bombardement atomique : le 6 août Hiroshima et le 9 août Nagasaki 1945 (Japon) sont rayées de la carte par les Américains.}", "", "19450809");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945", "", "Fin WW2", "", "Fin de la Seconde Guerre Mondiale le 2 septembre 1945.}", "H", "19450902");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1945-1946", "", "Plankalkül", "", "Invention par Konrad Zuse (De) en 1945/46 d'un première forme de langage de programmation de haut niveau indépendant de la machine d'exécution: le Plankalkül et utilisation de celui-ci pour formuler des programmes non numériques}", "", "");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1946", "", "ENIAC", "USA", "Création de l'ordinateur ENIAC (Electronic Numerical Integrator Analyser and Computer) par John Presper Eckert et John Mauchly à l'université de Pennsylvanie (USA). C'est le premier calculateur géant entièrement électrique qui répondait à l'architecture définie par Alan Turing. EXPOT : photo sépia collection Unisys ENIAC}", "HW", "19460000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1946", "", "Fondation de EMCC", "Univac", "Fondation de Eckert-Mauchly Computer Corporation (EMCC) par les créateurs de l'ENIAC.}", "Ent", "19460000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1946", "Casio", "", "Japon", "Kashio Seisakujo (Casio) est fondée à Mitaka, Tokyo (Japon).}", "Ent", "19460000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1946", "", "", "BE", "Début des travaux pour la création d'une machine à calculer électronique de type Mark I, « la Machine Mathématique IRSIA-FNRS » à la Bell-Telephone à Anvers. EXPOT : M.G. a des photos}", "HW", "19460000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1946", "IBM", "", "USA", "IBM (International Business Machines) présente la calculatrice électronique 603 en mai 1946.}", "Ca", "19460500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1947", "Bell", "Transistor", "USA", "Le transistor est inventé par les américains John Bardeen, William Shockley et Walter Brattain, chercheurs de la compagnie Bell Telephone (USA), le 23 décembre 1947.}", "E", "19471223");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1948", "", "", "Autriche", "Début de la commercialisation de la Curta, elle capable d'effectuer les quatre opérations de base. Une calculatrice mécanique rotative portable. Elle a été surnommée « le moulin à poivre ».}", "Ca", "19480000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1948", "IBM", "IBM SSEC", "USA", "IBM (International Business Machines) dévoile le Selective Sequence Electronic Calculator (SSEC), un ordinateur électromécanique.}", "E", "19480000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1948", "", "", "DE", "Plankalkül de Konrad Zuse (De): première proposition d'un langage de programmation de haut niveau.}", "Lang prog", "19480000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1948", "", "SSEM “Baby”", "UK", "Le SSEM (Small-Scale Experimental Machine aussi appelée « Baby » ou Manchester Mark I) est construite le 21 juin 1948, à l’université Victoria de Manchester (UK). Le SSEM exécute un programme enregistré en mémoire centrale selon l'architecture Von Neumann.}", "HW", "19480621");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1949", "", "", "BE", "Georges R. Boulanger (1909-1982) crée à la Faculté polytechnique de Mons, le premier cours de « calcul mécanique » (précurseur de l'« informatique »).}", "H", "19490000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1949", "", "", "", "Les ordinateurs futurs pourraient ne pas peser plus d'une tonne et demie. Le PDG de Popular Mechanics, 1949.}", "H", "19490000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1949", "", "", "UK", "George Orwell publie son roman 1984 (le monde sous surveillance du Big Brother!).}", "H", "19490000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1949", "", "EDSAC", "UK", "EDSAC (Electronic Delay Storage Automatic Calculator) fut construit par Maurice Vincent Wilkes en avril 1949 (UK); il s'était inspiré de l'EDVAC.}", "HW", "19490400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "Bull", "", "", "Bull fabrique ses petits relais destinés à remplacer tous ses relais téléphoniques.}", "E", "19500000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "Remington", "", "USA", "Remington Rand acquiert la Eckert-Mauchly Computer Corporation.}", "Ent", "19500000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "Burroughs", "", "USA", "Création de la Sensimatic Burroughs, une machine comptable. EXPOT}", "HW", "19500000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "", "SEAC", "", "Construction du SEAC : Standard Eastern Automatic Computer pour la résolution de problèmes météorologiques par l'U.S. National Bureau of Standards (NBS) aux USA.}", "HW", "19500000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "", "", "", "L'anglais Alan Turing définit dans son article L’ordinateur et l'intelligence le « jeu de l'imitation » ou « test de Turing ». C'est un test d’intelligence artificielle, il consiste à mettre en confrontation verbale un humain avec un ordinateur à l’aveugle.}", "T", "19500000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1950", "NPL", "", "UK", "Le 10 mai 1950, Pilot ACE (Pilot Automatic Computing Engine) dessinée par l'anglais Alan Turing au National Physical Laboratory (NPL) exécute son premier programme.}", "HW", "19500510");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "MBLE", "", "BE", "« La lampe Brabant » devient La Manufacture Belge des Lampes Électriques (MBLE), société importante dans la fabrication de composants électroniques.}", "Ent", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "Bell", "", "BE", "Décision par le FNRS et l'IRSIA de subsidier la construction d'un ordinateur digital numérique universel par Bell Telephone (Anvers).}", "Ent", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "", "", "USA", "Mise en service du Whirlwind I, un ordinateur à tubes à vide mis au point par le Servomechanisms Laboratory du MIT (USA) pour le compte de l’US Navy. Il sera utilisé dans le système d'alarme militaire SAGE.}", "Ent", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "Texas Instruments", "", "USA", "La société GSI (Geophysical Service Inc) prend le nom de Texas Instruments Incorporated.}", "Ent", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "", "", "BE", "Baudouin de Belgique (1930-1993) devient le cinquième roi des Belges.}", "H", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "Burroughs", "Fondation de la BEAM", "USA", "La création de la BEAM (Burroughs Electronic Accounting Machine) marque le début de l'informatique chez Burroughs.}", "HW", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "", "Mark I", "UK", "Le Ferranti Mark I (Manchester Mark I) est commercialisé.}", "HW", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "Remington Rand", "UNIVAC 1", "USA", "UNIVAC I (Eckert & Mauchly) par Remington Rand Corporation est mis sur le marché (15 exemplaires). C'est le premier ordinateur avec dérouleur de bandes magnétiques (métalliques).}", "HW", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1951", "", "Cybernétique", "FR", "Norbert Wiener, lors d'un congrès inaugural à Paris, établit les bases de la cybernétique, l'étude des mécanismes de la communication et régulation dans les machines et les êtres vivants.}", "T", "19510000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "", "", "", "EXPOT : Racam avionics SYSCAL 68.1219.}", "", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "IBM", "", "USA", "Thomas J. Watson Junior succède à son père comme PDG d'IBM (International Business Machines).}", "Ent", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "Sony", "", "", "Ibuka et Morita (qui créeront la société Sony) fabriquent le premier poste de radio portable à piles à transistors qui sera un succès au Japon.}", "Ent", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "CERN", "Fondation du CERN", "", "Création, par 11 États européens, de l'European Organization for Nuclear Research (CERN), installé à Genève.}", "Ent", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "Texas Instruments", "", "USA", "Texas Instruments acquiert la licence du transistor et commence à construire des semi-conducteurs.}", "Ent", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1952", "", "", "", "Dépôts du premier brevet des codes-barres.}", "", "19520000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1953", "Burroughs", "", "USA", "Burroughs Adding Machine Company devient la Burroughs Corporation.}", "Ent", "19530000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1953", "Bull", "", "", "Commercialisation du calculateur électronique Gamma 3 de Bull utilisant des diodes au germanium. Les circuits logiques à lampes étaient organisés en tiroirs que l'on pouvait ouvrir afin de facilité l’entretien et le refroidissement des tubes.}", "Ent", "19530000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1953", "", "", "", "RCA (Radio Corporation of America) est le premier à fabriquer en série des mémoires à tores de ferrite.}", "", "19530000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1953", "", "", "USA", "Dwight D. Eisenhower (1890-1969) devient président des USA de 1953 à 1961.}", "H", "19530000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1953", "IBM", "", "USA", "Présentation de l'IBM-701 (créé par J.A. Haddad & N. Rochester) le 27 mars 1953, 19 exemplaires seront vendus.}", "HW", "19530327");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1954", "", "", "", "EXPOT : règle à calculer SYSCAL : 68.1052.}", "", "19540000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1954", "IBM", "IBM-704", "USA", "Création de l'IBM-704 en novembre 1954, le premier ordinateur de série doté de capacités d’arithmétique en virgule flottante, utilisant les mémoires à tores de ferrite.}", "S", "19541100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1955", "", "FORTRAN I", "", "Diffusion du FORTAN I conçut chez IBM (International Business Machines) sous la direction de John Backus.}", "Lang prog", "19550000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1955", "Remington/Sperry", "", "USA", "Remington Rand est racheté par Sperry Corporation afin de former une firme nommée « Sperry Rand » (ou simplement « Sperry »).}", "Ent", "19550000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1955", "", "", "Be", "Inauguration de la Machine Mathématique de l'IRSIA-FNRS en février 1955.}", "HW", "19550200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1955", "IBM", "Mot ordinateur", "Fr", "Invention du mot ordinateur par Jacques Perret en France à la demande d'IBM (International Business Machines).}", "T", "19550416");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1956", "Laboratoire Shockley", "Silicon Valley", "USA", "William Shockley (1910-1989) ouvre le Laboratoire Shockley sur les semi-conducteurs à Palo Alto (Californie, USA) dans ce qui allait devenir par la suite la Silicon Valley.}", "Ent", "19560000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1956", "", "", "Be", "G. R. Boulanger publie Vers les usines sans hommes ou l'automation clef de voûte de la productivité de demain dans la Revue de la société royale belge des ingénieurs et des industriels.}", "T", "19560000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1956", "", "IA", "USA", "L'Intelligence Artificielle trouve son nom, sa mission et son concept lors de la conférence de Dartmouth (USA) de 1956.}", "T", "19560000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1956", "IBM", "IBM RAMAC 305", "USA", "Début de la commercialisation, le 14 septembre, de l'IBM RAMAC 305, c'est le premier ordinateur avec disque dur à têtes fixes le RAMAC 350 (Ramdom Access Method of Accounting and Control). Il s'est en vendu à plus de 1.000 exemplaires en 5 ans.}", "S", "19560914");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "DEC", "Fondation DEC", "USA", "Fondation de DEC (Digital Equipment Corporation) par Ken Olsen.}", "Ent", "19570000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "", "Spoutnik", "URSS", "Lancement du Spoutnik, premier satellite artificiel par l'URSS, le 4 septembre 1957.}", "H", "19570000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "", "", "", "J'ai parcouru le pays de long en large et parlé avec les meilleures personnes, et je peux vous assurer que l'informatique est une lubie qui ne durera pas plus d'un an. Éditeur chez Prentice Hall.}", "T", "19570000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "", "Fondation de CDC", "", "Création de la Control Data Corporation (CDC) avec Seymour Cray (1925-1996) comme designer d’ordinateurs, ils se spécialisent dans les grands ordinateurs.}", "", "19570000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "", "", "BE", "L'Association International de Cybernétique créée par l'UNESCO établit son siège à Namur le 6 janvier 1957.}", "T", "19570106");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1957", "Casio", "Casia 14-A", "", "Casio commercialise la première calculatrice de bureau toute électrique et compacte: la Casio 14-A en juin 1957.}", "Ca", "19570600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "", "DARPA", "USA", "Fondation en févier, au cœur de la guerre froide, de la DARPA (Defense Advanced Research Projects Agency), une agence du département de la Défense des États-Unis, créée afin de développer les technologies. Le DARPA sera à l'origine d'ARPANET et bien d'autres projets.}", "Ent", "19580200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "Texas Instruments", "Prototype du Circuit intégré", "USA", "Premier prototype de circuit intégré inventé par Jack Kilby.}", "HW", "19580000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "", "Langage LISP", "USA", "John McCarthy au MIT (USA), met au point LISP : un langage de programmation adapté à l'utilisation des listes. Il est fort utilisé en intelligence artificielle.}", "Lang prog", "19580000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "Brookhaven National Laboratories", "", "", "Au Brookhaven National Laboratories, à New York (USA), un physicien nommé Willy Higinbotham programme un jeu de tennis sur l’écran d’un oscilloscope, pour distraire les utilisateurs durant les portes ouvertes du laboratoire.}", "SW", "19580000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "", "", "", "Exposition universelle et internationale de Bruxelles, première démonstration public de l'IBM RAMAC au pavillon américain.}", "", "19580000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1958", "", "", "", "Création par Bull de la trieuse de cartes perforées D3. Elle permet plusieurs sélections en un seul passage des cartes. EXPOT}", "", "19580000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1959", "Burroughs", "", "USA", "Burroughs est pionnier dans l'utilisation de la reconnaissance de caractères à encre magnétique (MICR).}", "Ent", "19590000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1959", "", "", "", "Fin des spécifications du COBOL, en septembre 1959, par le comité CODASYL, largement inspiré des travaux de Grace Hopper et de Bob Berner. Expôt : programme COBOL}", "Lang prog", "19590000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1959", "", "CDC1604", "", "Control Data Corporation (CDC) commercialise le 16 octobre son premier gros calculateur scientifique : le CDC1604. C'est le premier ordinateur commercial entièrement transistorisé. C'est également le premier des ordinateurs hautes performances développés par Seymour Cray (1925-1996).}", "", "19591016");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "", "", "EXPOT : règle de calcul SYSCAL 68.1077.}", "", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "", "", "Création de la Société belge d'économie et de mathématiques appliquées (SOBEMAP) qui se spécialise dans le développement de software.}", "Ent", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "Siemens", "", "", "Création d'une usine Siemens à Oostkamp (Flandre-Occidentale) pour produire du matériel de télécommunication.}", "Ent", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "Projet Xanadu", "USA", "L'américain Ted Nelson (1937- ) commence a développer l'idée du projet Xanadu : un système d'information permettant le partage instantané et universel de données informatiques. Son projet reprend l'idée de l'hypertexte (dont il est l'auteur du terme). Le but du projet Xanadu est de construire un système universel de publication en hypertexte: une bibliothèque virtuelle capable d'accueillir une infinité de documents, dans lesquels il sera possible se déplacer grâce aux liens hypertextes, et dont les auteurs seront rémunérés automatiquement grâce à un \"mécanisme de micro-paiement de royalties\".}", "Ent", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "PDP-1", "", "Commercialisation du PDP-1 construit en 1959, par Ken et Sam Olsen chez DEC. 50 exemplaires seront construits et vendus pour 120.000 $, l'unité.}", "HW", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "ALGOL 60", "", "Fixation des normes du langage de programmation ALGOL 60, il sera surtout utilisé en Europe. Voici un langage très en avance sur son temps, il n'a pas seulement été une amélioration de ses prédécesseurs mais il sera aussi meilleur que presque tous ses successeurs. C. A. R. Hoare (qui a écrit le premier compilateur complet pour l'Algol 60).}", "Lang prog", "19600000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "", "Laser", "USA", "Theodore Harold Maiman, un Américain, réalise en mai 1960, le premier LASER (Light Amplifcation by Stimulated Emission of Radiation). Cette invention permettra la lecture des codes barres, la création des laserdiscs, des disques compacts, etc.}", "H", "19600500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1960", "Bull", "Gamma 60", "FR", "Livraison, par Bull, du premier Gamma 60, le 20 novembre 1960, à la SNCF (Société nationale des chemins de fer français) ; il y aura 16 autres exemplaires livrés. C'est le premier ordinateur qui permet la multiprogrammation, et un des premiers à embarquer plusieurs processeurs. Il permettait de gérer en parallèle plusieurs unités d'entrée et de sortie.}", "Ent", "19601120");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1961", "IBM", "", "", "IBM (International Business Machines) débute la fabrication de la machine à écrire électrique à boule interchangeable (chaque boule représente un jeu de caractères). Cela représente une innovation majeure pour tout ce qui regarde l’impression de données. Changer les boules permet de changer de police de caractères rapidement. Ce mécanisme avait déjà été inventé et utilisé par George Canfield Blickensderfer (1850–1917) en 1892.}", "C", "19610000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1961", "Bull", "CMC7", "", "La Compagnie des Machines Bull lance le CMC7 (Caractères Magnétiques Codés à 7 bâtonnets), un système d'impression et de reconnaissance de caractères adapté par l'association des banques françaises. Le code utilise de l'encre magnétique ce qui permet de continuer à lire sans erreur ces caractères même si quelqu'un y a superposé un trait de crayon, de stylo ou une tache.}", "C", "19610000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1961", "", "JFK président", "USA", "John F. Kennedy devient président des USA.}", "H", "19610000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1961", "Philips", "", "BE", "Création de la Tour spatiodynamique et cybernétique de Liège par l'artiste Nicolas Shöffer (avec la participation de Philips), d'autres tours ont été réalisées.}", "H", "19610000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1961", "", "CTSS", "", "Démonstration du premier système en temps partagé : CTSS (Compatible Time Sharing System) au MIT (USA) sur un IBM7094 modifié.}", "SW", "19610000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "", "BE", "Création du laboratoire de recherches de la MBLE à Bruxelles avec des ingénieurs qui avaient créé la machine mathématique IRSIA-FNRS (MMIF) à Anvers.}", "Ent", "19620000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "Langage API", "", "Création du langage de programmation APL de Kenneth Iverson à Harvard (USA) conçu pour manipuler des tableaux.}", "Lang prog", "19620000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "UNIVAC 1004", "", "Remington Rand-Univac produit un ordinateur de gestion : l'UNIVAC 1004, c'est un système hybride qui aura du succès.}", "", "19620000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "Mot informatique", "Fr", "Naissance du mot informatique d'information et automatique par Philippe Dreyfus (Fr).}", "T", "19620000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "", "", "Charles Bachman termine chez GE (General Electric), la première version de sa base de données en réseau : IDS. Elle sera disponible progressivement sur tous les systèmes produits par GE, puis par Honeywell Bull alors en vers IDS2.}", "", "19620000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "", "Spacewar!", "", "Le jeu Spacewar !, l'un des premiers jeu vidéo, est créé en décembre 1962, par des étudiants du MIT (USA). Ils font la démonstration de Spacewar sur un PDP-1 (Programmed Data Processor-1).}", "SW", "19621200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1962", "RadioShack", "", "USA", "Tandy Corporation achète RadioShack, une chaîne de magasins de composants électroniques et se lancera plus tard dans la commercialisation d'ordinateurs en kit.}", "Ent", "19630000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "Philips", "Cassette audio", "P-B", "Philips lance la cassette audio. Elle contient deux bobines où est enroulée une bande magnétique. Elle permet d'enregistrer et d'écouter du son. Elle servira aussi de support informatique avant que les disquettes 3'5 ne la remplace définitivement. Expot : cassettes}", "Ent", "19630000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "", "Invention de la souris", "", "Douglas Engelbart invente la souris, un système de pointage, la première commercialisation ne se fera qu'en 1973.}", "", "19630000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "", "Réseau SAGE", "USA", "Déploiement complet du réseau de défense continental SAGE (USA) un dispositif militaire de surveillance et de défense aérienne. Il s’agit du premier logiciel de grande envergure, comportant près de 500.000 instructions, ce qui était considérable pour l’époque.}", "SW", "19630000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "", "Sketchpad", "USA", "Au MIT (USA), sur un TX-2, Ivan Sutherland crée le premier logiciel de dessin : le Sketchpad.}", "SW", "19630000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "", "Discours de M.L. KIng", "USA", "Discours I have a dream de Martin Luther King, le 28 août 1963, devant le Lincoln Memorial à Washington (USA) durant la marche pour l'emploi et la liberté.}", "H", "19630828");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1963", "GE", "GE 400", "USA", "GE (General Electric) livre en décembre 1963, les premiers systèmes de sa série GE400. En Belgique, un GE 400 est livré à Hainaut-Sambre (entreprise sidérurgique) en 1964.}", "Ent", "19631200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1964", "RAND Corporation", "", "USA", "Commercialisation de la première tablette graphique, la RAND Tablet, elle coûtait 18.000$.}", "HW", "19640000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1964", "", "CDC 6600", "", "Le CDC 6600 est un superordinateur scientifique de Control Data Corporation, conçu par Seymour Cray. Originalité : mot de 60 bits et traitement d'instructions en parallèle. On est dans le multitâches.}", "Lang prog", "19640000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1964", "CDC", "Langage BASIC", "", "Fin des spécifications du BASIC (Beginner's All-purpose Symbolic Instruction Code) à l'université de Dartmouth (New Hampshire, USA) par John G. Kemeny and Thomas E. Kurtz, le premier mai. Il a été créé pour que les utilisateurs ne possédant pas de grandes connaissances techniques en informatique soient capable d'utiliser un ordinateur dans un but d'enseignement ou de recherche. C'était assez nouveau à l'époque. Une grande partie des micro-ordinateurs du milieu des années 70 (Olivetti P6060, Tektronix 4051, IBM 51001, Commodore Pet, les GE, etc.) seront équipés d'un BASIC.}", "SW", "19640000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1964", "Bull GE", "Bull-GE", "", "Prise de contrôle en novembre 1964, de Bull par GE (General Electric), naissance de Bull-GE résultant de l'Affaire Bull.}", "Ent", "19641100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "", "Enoncé de la loi de Moore", "USA", "Gordon E. Moore énonce la Loi de Moore : le nombre de transistors intégrables sur une seule puce double chaque année. En 1975, il corrigera plus tard, « chaque année » par « tous les 2 ans ». Expot : dessin de la loi}", "T", "19650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "Burroughs", "ILLIAC IV", "USA", "Lancement par Burroughs de l'ILLIAC IV, l'un des plus vieux prototypes d'ordinateur massivement parallèle. Il fait partie d'une série de superordinateurs construits à différents endroits. Au total, 5 ordinateurs de ce type ont été construits entre 1951 et 1974.}", "Ca", "19650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "Bull/GE", "GE115", "", "Commercialisation du GE115 qui est une évolution de l'ELEA d'Olivetti (Italie).}", "Ent", "19650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "IBM", "IBM360", "USA", "IBM (International Business Machines) lance la série IBM360 : un calculateur d'IBM qui comportait de nombreuses innovations et rencontra un énorme succès pour l'époque dont l'architecte principal est Gene Amdahl. Cette série couvrit progressivement une gamme d’ordinateurs plus étendue que celle de ses concurrents : du 360/20 au 360/70. Le plus petit modèle le 360/20 n'était pas entièrement compatible avec le reste de la série. La série IBM 360 impose deux nouveaux standards technologiques l'octet (8 bits) remplace le caractère de 6 bits : code interne en hexadécimal. Les bandes magnétiques passent de 7 à 9 voies et le codage des bandes s’aligne sur celui de l'IBM. L'Université de Liège sera le premier acheteur belge de l'IBM360. Expot : front panel}", "HW", "19650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "", "IBM1130", "", "Lancement de l'IBM1130, un petit ordinateur à vocation scientifique. Son faible coût l'a fait adopter aux applications de gestion (disponibilité du langage RPG) et connectable à de gros ordinateurs IBM (International Business Machines). Ce fut un très grand succès commercial.}", "", "19650000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "DEC", "PDP-8", "USA", "Lancement du PDP-8, le 22 mars 1965, par Digital Equipment, ce sera son premier mini-ordinateur à grand succès. Il utilisait comme unité de pupitrage un télétype ASR33 et comportait un disque dur fixe complété de DECtapes, bandes magnétiques adressables.}", "HW", "19650322");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1965", "Casio", "Casia 001", "Japon", "Casio sort la 001 en septembre 1965, une calculatrice de bureau entièrement électronique avec un affichage de 10 chiffres via des tubes NIXIE. Expot : tube NIXIE}", "Ca", "19650900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1966", "", "Fondation de la CII", "", "Création de CII (Compagnie International d’Informatique) par l’État Français.}", "", "19660000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1966", "", "Début de Star TRek", "USA", "Début de la série Star Trek créée par Gene Roddenberry.}", "H", "19660000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1966", "HP", "HP-2116", "USA", "HP produit son premier mini-ordinateur, le HP-2116.}", "HW", "19660000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1966", "Bull", "", "", "Sortie de l'ordinateur Bull Gamma 55 en mai 1966. Cette machine très originale compta parmi les premiers ordinateurs que l'on pouvait installer dans un environnement de bureau. Il eut un succès certain en France et au Benelux.}", "Ent", "19660500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1966", "", "", "", "On estime à 1.000 le nombre d’ordinateurs installés en Belgique en décembre 1966.}", "", "19661200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "", "", "", "Siemens rachète la société Zuse KG.}", "", "19670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "", "", "", "Fin des spécifications de PL/1 d'IBM (International Business Machines) qui le met à disposition sur ses séries 360 puis 370. C'est un langage puissant synthèse du COBOL, Fortran et ALGOL inspirant des futurs langages mais IBM (International Business Machines) échoua dans son objectif d'en faire un langage universel.}", "Lang prog", "19670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "", "LOGO", "", "Création du LOGO (langage d'initiation à la programmation avec une tortue graphique) de Wally Feurzeig et Seymour Papert au MIT (USA).}", "Lang prog", "19670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "", "", "", "EXPOT : Règle à calcul SYSCAL 68.1186.}", "", "19670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "", "Disquette 8 pouces", "USA", "Premier floppy disk (disquette souple de 8 pouces) par IBM (International Business Machines). C'est un support de stockage amovible de données informatiques pouvant contenir 80.000 caractères. Expot:}", "S", "19670000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "Bull", "", "Angl", "Ouverture à Londres en septembre 1967, du premier Centre Time Sharing en Europe par Bull-GE suivi de peu par celui d'IBM (International Business Machines).}", "Ent", "19670900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1967", "Casio", "", "Japon", "Vente des premières calculatrices électroniques programmables de bureau par Casio en octobre 1967.}", "Ca", "19671000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1968", "MITS", "Mother of all demos", "USA", "Télévidéo-conférence entre Palo-Alto et San Francicso (USA) le 9 décembre 1968 : The mother of all demos où Douglas Engelbart présente avec son équipe la souris, les documents hypermédia structurés, les systèmes de fenêtrage, le partage d’écran, le courrier électronique et d’autres éléments de NLS (oN-Line System).}", "Ent", "19680000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1968", "", "", "USA", "Sortie du film : 2001, l'Odyssée de l'espace de Stanley Kubrick.Robert Noyce, Gordon Moore, Andy Grove et Arthur Rock fondent Intel Corporation.Ed Robert fonde « Micro Instrumentation Telemetry Systems » (MITS), société qui lancera plus tard l'Altair 8800.}", "SW", "19681209");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1969", "", "", "", "Procédure américaine anti-trust contre IBM (International Business Machines) qui durera 13 ans. IBM pour contrer cette attaque fera une refonte de ses tarifs en séparant les prix des matériels, des logiciels et des services. Tous les concurrents suivront cette ligne à partir de 1971. Le marché de l’informatique s'ouvre alors aux marchés du logiciel.}", "", "19690000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1969", "", "Apollo UI", "USA", "La mission Apollo XI dépose Neil Armstrong et Edwin Aldrin sur la Lune le 21 juillet 1969.}", "H", "19690000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1969", "", "", "USA", "Début des robots industriels : apparition des Automates Programmables Industriels (API) aux USA où ils répondaient aux désirs des industries de l’automobile de développer des chaînes de fabrication automatisées.}", "HW", "19690000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1969", "Bell", "UNIX", "USA", "Ken Thompson développe Unix : un système d'exploitation multitâche et multi-utilisateur. Le nom a été choisi par Brian Kernighan à la suite d'un jeu de mots « latin » avec Multics : un autre système d’exploitation créé à partir de 1964 dont s'est inspiré Unix.}", "SW", "19690000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1969", "", "ARPAnet", "USA", "Première liaison entre deux ordinateurs distants (Los Angeles – Stanford, USA) le 2 septembre 1969, liaison qui est à l'origine du projet ARPAnet (Advanced Research Projects Agency) de l'armée américaine.}", "SW", "19690900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "", "", "", "Le gouvernement Belge signe avec les départements informatiques de Siemens et Philips un contrat de progrès : ils s'engagent à créer des usines et en compensation l'état et les parastataux commanderont du matériel informatique chez eux. Siemens construit des usines à Baudour (Hainaut) et Lanklaar (Limbourg) ; et Philips à ????}", "", "19700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "", "", "USA", "Xerox ouvre le Palo Alto Research Center (PARC), aux USA.Sa mission est « d'inventer le bureau du futur » en regroupant de grand moyen financier et de grands chercheurs. Cependant, Xerox ne réussira pas toujours à valoriser ses inventions d'autres y parviendront.}", "Ent", "19700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "", "", "BE", "Création de l'Institut d'Informatique des FUNDP (Namur)}", "H", "19700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "DEC", "PDP-11", "USA", "Lancement du mini-ordinateur : PDP-11, il travaille en Time Sharing (une approche permettant de simuler le partage par plusieurs utilisateurs du même processeur)}", "HW", "19700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "", "", "Be", "Baudouin Fraeijs de l’Université de Liège met au point une méthode de « conception assistée par ordinateur » (CAO) pour la SABCA.}", "SW", "19700000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1970", "Bull Honeywell", "", "", "Honeywell rachète les parts que GE avait chez Bull en août 1970. Bull devient Honeywell Bull.}", "Ent", "19700800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "Texas Instruments", "", "USA", "Production de la première calculatrice de poche par Texas Instruments, elle est destinée au marché professionnel.}", "Ca", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "", "", "Be", "Début des travaux d'informatique appliqués à la Bible et aux textes par des moines de Maredsous.}", "Ent", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "Xerox", "Imprimante laser", "USA", "Invention de l'imprimante laser, qui utilise un procédé d’impression xérographique (ou électrophotographique), par Gary Starkweather chercheur du Xerox Palo Alto Research Center (USA).}", "IO", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "Nutting Associates", "", "", "Nutting Associates lance le premier jeu vidéo pour borne d’arcade (un meuble contenant un jeu vidéo payant) de l’histoire : Computer Space.}", "SW", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "", "", "", "Création de machines spécialisées dans le traitement de texte par la société Wang (Rhode Island, USA). Elles ont eu un grand succès, car il y avait une bonne ergonomie dans ses polices à l'écran, un usage intelligent de la double brillance et de l'inversion vidéo, ainsi qu'un système de recherche qui s'exécutait au fur et à mesure de la frappe des caractères de la chaîne cherchée.}", "", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "", "email", "USA", "L’américain Ray Tomlinson (1941-2016) invente l'e-mail en mettant le @ dans l’adresse pour séparer le nom de l'utilisateur et le nom de l'ordinateur.}", "C", "19710000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "Casio", "", "Japon", "Casio dévoile la première imprimante à jet d'encre, en avril 1971.}", "IO", "19710400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "", "Protocole FTP", "USA", "Invention du protocole FTP (File Transfert Protocol), le 16 avril 1971, par Abhay Bhushan au MIT. C'est un protocole de communication destiné à l'échange de fichiers informatiques sur un réseau TCP/IP.}", "SW", "19710416");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1971", "Intel", "CPU 4004", "USA", "Le premier microprocesseur est commercialisé : l'Intel 4004 avec 2300 transistors, le 15 novembre 1971.}", "HW", "19711115");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1972", "", "Langage Pascal", "", "Création du langage de programmation Pascal de Niklaus Wirth.}", "Lang prog", "19720000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1972", "", "", "BE", "La RTT met en service son centre de télécommunications spatiales à Lessive (Namur).}", "Ent", "19720000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1972", "", "Langage C", "USA", "Première version du langage de programmation C de Dennis Ritchie et Ken Thompson inventé pour réécrire UNIX.}", "Lang prog", "19720000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1972", "", "Casio Mini", "", "Casio sort la première calculatrice grand public : la « casio Mini ».}", "", "19720000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "Bull/R2E", "Micra N", "Fr", "Commercialisation du MICRAL N, le premier micro-ordinateur conçut François Gernelle dans la société R2E dirigée par André Truong.}", "HW", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "BBC", "CEEFAX", "UK", "Lancement de CEEFAX, un standard de télétexte, un service permettant de délivrer des informations sous format texte et animations, retransmis dans les signaux d'une chaîne de télévision.}", "C", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "Xerox", "Ethernet", "USA", "Ethernet, un protocole de réseau local à commutation de paquets, est créé par Robert Metcalfe au Xerox PARC.}", "C", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "SWIFT", "", "BE", "Naissance à Bruxelles de la Society for Worldwide Interbank Financial Telecommunications (SWIFT), elle ouvrira un réseau opérationnel du même nom en 1977. Expot : sécurité des systèmes}", "Ent", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "IBM", "Disque dur Winchester", "", "Commercialisation du disque dur IBM 3040 ou Winchester. Chaque partie avait une capacité de stockage de 30 Mo. IBM (International Business Machines) sort également, cette année-là, la série IBM370. expo}", "HW", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "", "Fondation d’UniData", "", "Création, en juillet, de UniData qui est la fusion des départements informatiques de CII, Siemens et Philips, cela sera un échec et dissous en 1975.}", "", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "Xerox", "XEROX Alto", "USA", "XEROX crée l'Alto un des premiers ordinateurs avec un écran, une interface graphique utilisateur proposant une métaphore du bureau et une souris. C'était un outil de recherche pour Xerox, un prototype de ce que serait plus tard le Xerox Star. Le père de l'Alto, Charles P. Thacker, a reçu le Prix Turing 2009 pour ses travaux sur cet ordinateur.}", "IO", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1973", "", "CP/M", "USA", "Gary Kildall crée son système d'exploitation: le CP/M. La révolution est qu'il l'a créé pour un type de processeur (Intel (80xx), mais aussi sur Zilog (Z80) et Motorola (68xx et 69xx)) et non pour un ordinateur. Il peut donc être utilisé sur différents types de machines. En 1976, il le vendra à IMS Associates Inc. pour l'ISMAI 8800.}", "Ent", "19730000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1974", "IBM", "SQL", "USA", "Le SQL, un langage d’interrogation de base de données, est proposé par Donald D. Chamberlin et Raymond F. Boyce.}", "Lang prog", "19740000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1974", "", "TCP/IP", "USA", "Vint Cerf et Robert Kahn publient un article A Protocol for Packet Network Intercommunication (USA)dans lequel ils décrivent le protocole TCP/IP. C'est un sous produit du projet ARPAnet, un protocole d'échange entre ordinateurs ou entre réseaux de différentes origines.}", "T", "19740000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1974", "Intel", "CPU 8080", "USA", "Intel commercialise le 8080 en avril, un des premiers microprocesseurs 8 bits véritablement utilisables dans un ordinateur qui cadençait à 2 MHz en avril 1974. Il sera à l'origine des premier PC comme l'Altair 8800.}", "HW", "19740400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1974", "Bull", "", "", "Le français Roland Moreno (1945-2012) dépose le brevet sur les \"objets portables sécurisés\". C'est cette technologie qui sera à l'origine, entre autres, de la carte à puce.}", "HW", "19740500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "", "USA", "Les rassemblements de passionnés d'informatique voient le jour : « Homebrew Computer Club » à Menlo Park, « Amateur Computer Group » au New Jersey, « Southern California Computer Society » à Los Angeles.}", "C", "19750000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "", "Be", "Mise en service de la centrale nucléaire Tihange-1.}", "E", "19750000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "Pong", "", "Le jeu Pong est proposé sur une console de salon (la Ping-O-Tronic). Il est d'abord commercialisé par Sears, puis par Atari, un an après.}", "", "19750000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "", "", "IBM (International Business Machines) lance le système 32, un petit système de gestion pour environnement bureau. Sa grande originalité ce sont des cartes perforées à dimension réduite.}", "", "19750000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "", "", "Le Data-Point 2200 est un petit système qui dispose d'un traitement de texte.}", "", "19750000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "MITS", "Altair 8800", "USA", "MITS Altair 8800 de Robert et Morrow (sans écran ni clavier) par MITS en janvier 1975. C'est un micro-ordinateur basé sur le microprocesseur Intel 8080 et vendu en kit électronique à quelques milliers d'exemplaires. Il est considéré comme l'un des premiers micro-ordinateurs vendus aux particuliers.}", "HW", "19750100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "", "", "BE", "Mise en service de la centrale nucléaire de Doel 1 (392 Mwe) le 15 février 1975.}", "E", "19750215");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "IBM", "", "USA", "Sortie en septembre 1975 de l'IBM 5100 Portable Computer, le premier ordinateur personnel portable d'IBM (International Business Machines). Encombrant et pesant 25 kg, il est plutôt transportable que portable. EXPOT : IBM5100}", "HW", "19750900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1975", "Intel", "IMSAI 8080", "USA", "Sortie en décembre de l'IMSAI 8080 : un des pionniers des micro-ordinateurs basés sur un microprocesseur Intel 8080 avec un bus S-100. C'est un clone du MITS Altair 8800.}", "HW", "19751200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "United Business Media", "Dr. Dobb’s", "USA", "Le premier numéro du « Dr. Dobb's Journal » est publié aux USA, un magazine sur l'informatique et la programmation qui sera publié jusqu'à la fin 2014.}", "C", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "Apple", "Fondation d’Apple", "USA", "Fondation de la société Apple par les américains Steve Jobs, Steve Wozniak et Ronald Wayne et lancement de l'Apple I.}", "Ent", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "", "Apparition d'un nouveau constructeur spécialisé dans les systèmes à tolérance de panne : TANDEM.}", "", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "ComputerLand", "", "USA", "Le premier magasin d’informatique ComputerLand ouvre ses portes en Californie (USA).}", "Ent", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "IMSAI", "", "USA", "Gary Kildall vend le CP/M au IMSAI.}", "Ent", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "BE", "Création de candidatures[1] en informatique au sein de la Faculté des sciences économiques, sociales et de gestion FUNDP}", "H", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "Cray", "", "USA", "Seymour Cray développe le Cray 1 un superordinateur à architecture vectorielle. Il est construit autour d'un processeur 64 bits cadencé à 83 MHz, doté de 8 Mo de mémoire vive et refroidi au fréon. Il atteint une puissance de calcul de 166 MFLOPS , soit la puissance moyenne d'un ordinateur de bureau vingt ans plus tard. Une des innovations de cette machine est sa forme en arc de cercle qui permet de réduire les longueurs des différents fils. Ce supercalculateur pesant près de 5 tonnes coûtait près de 9 millions de dollars à l'époque. Le 1er Cray-1 a été livré à « Los Alamos National Laboratory » et au total 16 machines furent produites.}", "HW", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "IBM", "", "USA", "IBM (International Business Machines) commercialise la première imprimante laser pour mainframe : l'IBM 3800.}", "IO", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "Exidy Games", "", "USA", "Exidy Games lance Death Race 2000, un jeu d’arcade de course automobile inspiré du film du même nom sorti en 1975. Le joueur gagne des points en écrasant des piétons. De nombreuses voix s’indignèrent contre le jeu, qui sera rapidement retiré du marché.}", "SW", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "USA", "L'américain Michael Shrayer crée le Electric Pencil, un des premiers logiciel de traitement de texte.}", "SW", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "USA", "Bill Gate (USA) écrit, An Open Letter to Hobbyists, dans cette lettre ouverte adressée aux membres du Homebrew Computer Club, il exprime sa consternation face à la généralisation des infractions du droit d’auteur, notamment envers les logiciels de sa compagnie.}", "T", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "", "Xerox ferme son département informatique et en cède la maintenance à Honeywell.}", "", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "", "", "", "IBM (International Business Machines) lance son premier mini-ordinateur, l'IBM Series/1. Il est compatible avec le PDP-11 de DEC. La marine américaine en fera grand usage. Expot : I&B}", "", "19760000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "Bull", "", "USA", "Fusion de Honeywell Bull et CII (Compagnie Internationale pour l'Informatique), le 30 juin 1976.}", "Ent", "19760630");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1976", "Microsoft", "", "USA", "Dépôt de la marque de commerce Microsoft par Bill Gates et Paul Allen en novembre 1976.}", "Ent", "19761100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "IBL 3000", "", "IBM (International Business Machines) lance la série de grand système : l'IBM 3000.}", "", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "", "USA", "Le premier système de communication téléphonie optique est installé au centre-ville de Chicago (USA).}", "", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "", "", "EXPOT : Imsai VDP 80 + Diablo 1550 (I&B) Imsai est l’ordinateur principal du film « War Game ».}", "", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "DAI", "", "EXPOT : DAI Imagination machine (I&B)}", "", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "", "", "Il n'y a aucune raison que des gens veuillent un ordinateur à la maison. Ken Olson, PDG et fondateur de DEC.}", "H", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "", "", "USA", "Sortie du premier opus de Star Wars, un film américain de science-fiction écrit et réalisé par George Lucas. À partir de l'an 2000, il est exploité sous le nom Star Wars, épisode IV : Un nouvel espoir. Le succès du VIIe épisode sorti en 2015 a été confirmé.}", "H", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "Commodore International", "", "USA", "Le Commodore PET 2001 (Personnal Electronic Transactor) est un ordinateur personnel (avec un clavier et un petit écran) commercialisé par Commodore International. En Europe, cet ligne ordinateurs est connue sous le nom de Commordore CBM. EXPOT : Pet-Commodore professional computer (I&B et Lemaire) EXPOT : Commodor 64}", "HW", "19770000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "Tandy/Radio Shack", "", "USA", "Steven Leininger crée le TRS-80 à Tandy/Radio Shack un des premiers micro-ordinateurs. EXPOT : (si modèle 1) TRS-80 Radio Scharck}", "HW", "19770001");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "Apple", "Fondation de Apple", "USA", "Apple Computer Inc. est constituée sous forme de société, à Cupertino, le 3 janvier 1977.}", "Ent", "19770103");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "Bull", "", "", "CII-Honeywell Bull étend le système d'exploitation GCOS7 à 5 modèles en mars 1977 et et à disposition un gestionnaire transactionnel online (TDS) et une base de données IDS-II.}", "Ent", "19770300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1977", "Apple", "Apple II", "USA", "Apple lance l'Apple II lors du West Coast Computer Fair (une conférence annuelle de l'industrie informatique au USA), le 17 avril 1977. C'est le premier ordinateur personnel habillé de manière attractive, base du succès de la firme. EXPOT : Apple II original}", "HW", "19770417");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978 env", "", "", "", "EXPOT : Perkin-Elmer 550 terminal (I&B)}", "", "19780000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "HP", "", "USA", "William Hewlett et David Packard, les fondateurs de HP, prennent leur retraite et passent le relais à John A. Young en conservant des fonctions honorifiques dans le groupe.}", "Ent", "19780000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "", "", "", "Publication de l'ouvrage de référence sur le langage de programmation C :The C Programming Language de Brian Kernighan et Dennis Ritchie qui aida à populariser le langage.}", "Lang prog", "19780000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "", "Fortran 77", "", "Première version du Fortran normalisée ASCII qui devient la norme : le Fortran 77.}", "", "19780000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "Cinematronics", "", "USA", "Cinematronics lance Space Wars. Le jeu utilise un affichage dit vectoriel, basé sur des segments lumineux, plutôt que des pixels (points lumineux), utilisés jusque-là.}", "SW", "19780000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "DEC", "Premier spam", "USA", "Gary Thuerk envoie le premier message spam, (courriel indésirable ou pourriel), alors qu'il travaille pour DEC, le 3 mai 1978. Il a envoyé son message à près de la totalité des utilisateurs d'ARPAnet vivant sur la côte ouest des États-Unis, soit environ 600 personnes. Le mot spam est issu d'un sketch des Monty Python (de 1972) mettant en scène la viande précuite en boîte de la marque Spam.}", "C", "19780503");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1978", "Microsoft", "", "USA", "Le chiffre d’affaires de Microsoft, une société qui se consacre uniquement aux logiciels, dépasse le million de dollars en décembre 1978.}", "Ent", "19781200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "", "Usenet", "USA", "Création de Usenet par les étudiants des Universités de Duke et de Caroline du Nord (USA), un système en réseau de forums réalisé bien avant l'Internet. Usenet est toujours utilisé aujourd'hui.}", "C", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "", "Langage Ada", "", "Création du langage de programmation : Ada par Jean Ichbiach et son équipe chez CII-Honeywell-Bull. Il est créé pour des systèmes en temps réel et embarqué nécessitant un haut niveau de fiabilité et de sécurité.}", "Lang prog", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "", "", "BE", "Création de l'Association en Gestion et en Informatique (AGI) à Louvain-la-Neuve.}", "SoI", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "Namco", "Pac-Man", "Japon", "Namco lance Pac-Man, un jeu vidéo très populaire : 300.000 unités du jeu sur bornes d’arcade sont vendues à travers le monde.}", "SW", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "", "", "", "IBM (International Business Machines) met à disposition son système 38, avec OS AS/400. Système de moyenne gramme succédant au système 34. L'architecture est originale et c'est un grand succès commercial.}", "", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "", "VisiCalc", "USA", "Création du VisiCalc, un tableur, par Dan Bricklin, qui sera vendu à 35.000 exemplaires. D'abord diffusé avec l'Apple II, il est à la base du succès de cet ordinateur.}", "SW", "19790000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "Générale de Banque", "", "BE", "Création en juin 1979 de deux systèmes de paiement par carte de crédit par plusieurs banques en Belgique : la CGER (ASLK), la BBL et la Kredietbank lancent Bancontact (équipé par Bull), tandis que la Générale de Banque et le Crédit communal lancent Mistercash (équipé par IBM (International Business Machines)).}", "HW", "19790100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1979", "Tandy/Radio Shack", "TRS-80 Model II", "USA", "Tandy/Radio Shack commercialise le TRS-80 Model II qui était conçut pour une clientèle plus professionnelle. Il intègre deux lecteurs de disquette 8 pouces. Son microprocesseur Zilog Z80 passe à 4 MHz et il tourne sous TRSDOS II.}", "HW", "19791000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1980", "Microsoft/IBM", "", "", "Microsoft signe un accord avec IBM (International Business Machines) pour développer un système d'exploitation et un ensemble de programmes qui dirigent l'utilisation des capacités d'un ordinateur par des logiciels applicatifs. Microsoft achète QDOS, créé par Tim Paterson à la Seattle Computer Products et le rebaptise MS-DOS et développera d'autres logiciels.}", "", "19800000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1980 env", "", "", "", "EXPOT : Texas Intruments TI 99/4A (I&B)}", "", "19800000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1980", "Apple", "Apple III", "USA", "L'Apple III est présenté lors de la National Computer Conference à Anaheim (USA), le 19 mai 1980. Il ne sortira que 6 mois plus tard à cause de problèmes de production et sera un échec commercial à cause de différents problèmes techniques liés à la surchauffe.}", "Ent", "19800519");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "IBM/Microsoft", "", "USA", "L'IBM-PC (IBM5150) est présenté à New York (USA), lors d'une conférence de presse dans l'hôtel Waldorf-Astoria, le 12 août 1981. Il sera le premier ordinateur personnel produit à plusieurs millions d'exemplaires. Son architecture ouverte en a fait l'ancêtre de tous les compatibles PC. Il est issu d'une collaboration entre IBM (International Business Machines) et Microsoft.}", "Ent", "19800812");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "", "", "", "Honneywell-Bull sort un ordinateur de la gamme moyenne, équipé de disques et de bandes magnétiques : le DPS7-65 à bénéficier de la technologie de création des microcircuits en Tape Automated Bonding : des microcircuits portés en fabrication sur bande, technologie qui sera connue chez Bull comme Micropackaging.}", "", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "Compaq Computer", "Fondation de Compaq", "USA", "Rod Canion, Jim Harris et Bill Murto fondent Compaq Computer à Houston (USA), une société spécialisée dans la fabrication d’ordinateurs.}", "Ent", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "", "Reagan Président", "USA", "Ronald W. Reagan devient président des USA.}", "H", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "", "IBM PC", "IBM", "L'IBM PC est vendu à 1565$. Il arrivera sur le marché belge en 1983. EXPO : pub IBM EXPOT : IBM PC 5151 EXPOT : IBM PC 5322}", "HW", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "Osborne", "Osbborne 1", "USA", "Adam Osborne crée l'Osborne 1, le premier ordinateur portable ayant connu un succès commercial. Il pesait 11 kg. EXPOT : Osborne}", "HW", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "Nintendo", "", "Japon", "Shigeru Moyamoto crée Donkey Kong. Le héros, originalement nommé Jumpman, sera rebaptisé Mario par l’équipe de Nintendo.}", "SoI", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "", "", "", "Commercialisation du Sinclair ZX81, un petit ordinateur avec une télévision comme écran. EXPOT : sinclair ?}", "", "19810000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1981", "Casio", "", "", "Casio sort le premier dictionnaire électronique (TR-2000) en octobre 1981. EXPOT : TS-2000 I&B}", "C", "19811000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "", "Commodore 64", "", "Commercialisation du Commodor 64, le premier micro-ordinateur à grand succès. Il a des applications Basic et est fourni avec des jeux.}", "", "19820000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "", "", "", "Commercialisation des premiers CD audio par Philips et Sony qui se sont accordés sur un standard. C'est un disque optique utilisé pour stocker des données sous forme numérique. Les premiers périphériques (reader / writer) suivront, en 1988 seulement. Expot : CD}", "", "19820000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "Disney", "Tron", "USA", "Sortie du film Tron réalisé par Steven Lisberger. Un des premier film avec un grand nombre d'images de synthèses pour des éléments de fiction.}", "H", "19820000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "", "Flight Simulator", "", "Microsoft prend une licence de développement du logiciel Flight Simulator et diffuse la version 1.0. Ce logiciel sera de plus en plus sophistiqué au cours du temps.}", "", "19820000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "Sun Microsystems", "", "USA", "Fondation de Sun Microsystems, par 3 étudiants de l’Université de Stanford (USA), le 24 février 1982, la société sera rachetée plus tard par Oracle. Elle sera spécialisée dans la création de station de travail, de logiciel et créera le langage Java.}", "Ent", "19820224");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "Bull", "", "Fr", "Nationalisation de CII-Honywell-Bull par le gouvernement français en avril 1982.}", "Ent", "19820400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1982", "Adobe", "", "USA", "Adobe est fondée en décembre 1982, par John Warnock et Charles Geschke, qui avait quitté le Xerox PARC pour améliorer et commercialiser le langage de mise en forme PostScript, refusé par Xerox. Cette société fera une série d’acquisitions d'entreprises d'édition de logiciels. Le plus important apport d'Adobe est le nouveau standard de présentation de document : le PDF.}", "Ent", "19821200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Apple", "LISA", "USA", "Apple sort le LISA, l'un des premiers ordinateurs personnels à posséder une souris et une interface graphique qui avait été développée par le Xerox Parc. EXPOT : Apple Lisa}", "HW", "19830000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "", "", "USA", "ARPAnet bascule sur un nouveau système : les protocoles TCP et IP (Transmission Control Protocol Internet Protocol).}", "C", "19830000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "", "IBM DB2", "", "IBM (International Business Machines) introduit la base de donnée DB2, inspirée des principes de Edgar F. Codd. Ce logiciel aura un succès grandissant et contribuera au développement de bases de données de ce types.}", "", "19830000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Borland International", "Fondation de Borland", "USA", "Fondation de Borland International. L'entreprise créera un grand nombre de logiciels de développement. Elle lancera le Turbo Pascal, un environnement de développement intégré pour le langage Pascal en 1984. Sa puissance et son prix « démocratique » ont fait son succès.}", "Ent", "19830000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "IBM", "IBM PC-XT", "", "Lancement par IBM (International Business Machines) de l'IBM-PC-XT (modèle 5160).}", "HW", "19830000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "", "Lotus 1-2-3", "", "Au début de 1983, Lotus sort Lotus 1-2-3 pour MS-DOS. Lotus permettrait aussi des graphiques et la création d'une base de données. C'est un grand succès commercial jusqu'en 1988.}", "", "19830001");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Compaq", "", "USA", "Compaq commercialise, en janvier, un PC portable (transportable est plus juste), il était 100 % compatible avec l'IBM PC.}", "HW", "19830100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Microsoft", "", "USA", "Microsoft commercialise sa première souris en mai 1983. Contrairement à la souris d’Apple, elle possède deux boutons.}", "IO", "19830500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "", "", "", "Le 8 août 1983, une loi, présentée par le ministre de l’intérieur, Charles-Ferdinand Nothomb, donne une existence légale au Registre national des personnes physiques. Il s'agit d'une base de données qui contient les données concernant, entre autres, l’identité de toutes les personnes résidant légalement en Belgique.}", "", "19830808");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Microsoft logiciel", "Word", "", "Le logiciel Word est disponible sous MS-DOS en septembre 1983.}", "C", "19830900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1983", "Microsoft", "Windows 1.0", "", "Microsoft présente l’interface graphique Windows 1.0 pour son système MS-DOS en novembre 1983.}", "SW", "19831100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "", "", "", "Fondation de StarDivision par un programmeur allemand, il met à disposition un traitement de texte : StarWriter et d'autres composants qui formeront : StarOffice.}", "", "19840000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "", "", "", "MicroTech (Taïwan) vend le premier scanner de bureau à prix abordable : le MS-303, puis le premier lecteur OCR populaire de bureau.}", "", "19840000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "", "", "", "En septembre 1984, le réseau ARPAnet est divisé en une application militaire : le MILnet et une application privée l'ARPAnet qui n'est plus liée à l'administration américaine.}", "", "19840000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "Apple", "Macintosh", "USA", "Apple annonce le Macintosh le 22 janvier 1984 en présentant au « Super Bowl » sa publicité réalisée par Ridley Scott pour le lancement.}", "Ent", "19840122");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "", "Fondation de GNU", "USA", "Fondation de GNU (GNU’s Not UNIX) par Richard Stallman, popularisant le mouvement open-source.}", "Ent", "19849000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "Dell", "Fondation de Dell", "USA", "Michael Dell fonde le Dell Computer.}", "Ent", "19849000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "", "", "USA", "Sortie du film The Last Starfighter réalisé par Nick Castle avec beaucoup d'images de synthèse pour des éléments réels.}", "H", "19849000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1984", "HP", "HP-110", "", "Hewlett-Packard commercialise son premier ordinateur personnel : le HP-110; sa première imprimante à jet d'encre (Thinkjet); sa première imprimante laser, le HP LaserJet.}", "HW", "19849000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "", "Langage C++", "", "Première version de The C++ Programming Language, il est le premier livre d'informatique présentant le langage C++, écrit par l'inventeur du langage, Bjarne Stroustrup.}", "Lang prog", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Bull", "", "", "Bull lance l'imprimante non-impact magnéto-graphique MP6050,60,90 (série Mathilde). Expot : Mathilde}", "IO", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Microsoft", "", "", "Commercialisation de Excel, tableur de Microsoft, qui deviendra le standard.}", "SW", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Bull", "Micral 30", "", "Commercialisation du Bull Micral 30 sous MS-DOS.}", "HW", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "", "FSF", "", "Création de la Free Software Foundation liée à la création de logiciels libres sur base de la licence GNU.}", "", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "", "Cobol 85", "", "Définition du Cobol 85, version majeure pensée pour la programmation dite structurée.}", "", "19850000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "", "", "", "Zenith Data System introduit l’ordinateur réellement portable : le Z-171 (de 6,5kg). Expot : chez Bull.}", "", "19850500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Bull", "Bull DPS 90", "", "Sortie du Bull DPS 90 (5 modèles), sous GCOS 8 en juin 1985.}", "HW", "19850600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Apple", "Fondation de NeXT", "USA", "Steve Jobs quitte Apple pour fonder NeXT Inc en septembre 1985.}", "Ent", "19850900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1985", "Nintendo", "", "Japon", "Super Mario Bros est conçu par Shigeru Miyamoto en septembre 1985, il est l'un des premiers jeux de plateforme sorti sur NES (la console 8-bits de Nintendo) et est vendu avec la console.}", "SW", "19850900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Pixar", "", "USA", "Steve Jobs achète the Computer Graphics Division de George Lucas et établit une compagnie indépendante : \"Pixar.\"}", "Ent", "19860000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "", "", "", "Sortie du film Short Circuit réalisé par John Badham.}", "", "19860000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "", "", "", "Brain, le premier virus informatique s'attaquant au PC sous MS-DOS se répand via des disquettes. Il n'a pas été lâché volontairement.}", "", "19860000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Bull", "", "", "Commercialisation du PC Bull Micral 60, un clone du PC/AT. Il fut réalisé par une collaboration de Bull Transac à Villebon (Fr) et de R2E of America.}", "HW", "19860000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Nintendo", "", "Japon", "The Legend of Zelda est créé par Shigeru Miyamoto en février 1986.}", "SW", "19860200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Microsoft", "", "USA", "Microsoft entre en Bourse le 13 mars 1986. Le cours de l’action débute à 21 $ et termine la journée à 28 $.}", "Ent", "19860313");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "", "Catastrophe de Tchernobyl", "URSS", "Catastrophe nucléaire à Tchernobyl (Ukraine), le 24 avril 1986.}", "H", "19860426");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Burroughts/Unisys/Sperry", "", "USA", "Fusion de Sperry Rand et de Burroughs Corporation, le 26 mai 1986, afin de donner naissance à Unisys.}", "Ent", "19860526");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1986", "Bull", "", "", "Le bateau sponsorisé par Bull, L'Esprit d’Équipe, gagne le tour du monde à la voile en juillet 1986.}", "Ent", "19860700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "", "Disquette 3.5 pouces", "", "Les disquettes 3,5 pouces remplacent les disquettes 8 et 5,25 pouces.}", "", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "", "", "", "IBM (International Business Machines) lance l'OS multitâche : OS2 PS2, un compatible IBMPC, concurrent de UNIX et de Windows pour les PC hautes gammes et les serveurs.}", "", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "", "", "USA", "Rick Astley sort le clip de sa chanson Never Gonna Give You Up, aux USA. En 2007, un buzz se crée sur Internet autour du clip.}", "H", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "IBM", "PS/2 et OS/E", "", "IBM (International Business Machines) présente le PS/2 avec OS/2 compatibles avec le premier IBM PC.}", "HW", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "Apple", "Macintosh couleur", "USA", "Apple lance le Macintosh II, le premier Macintosh à supporter l'affichage couleur.}", "HW", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "CompuServe", "GIF", "", "Le GIF (Graphics Interchange Format) a été mis au point par CompuServe pour permettre le téléchargement d'images en couleur.}", "SW", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "", "", "", "Création de la société belge Lernout & Hauspie (L&H), pionnière dans le développement de reconnaissance vocale et de technologies informatiques de synthèse vocale. La société se trouva en situation de cessation de paiements, en 2001. EXPOT : boite logiciel (Bull)}", "", "19870000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "Microsoft", "", "USA", "Le nouveau logo Microsoft sort en mars 1987. Les employés protestent et lancent une campagne de sauvegarde du « Blibbet ». Le nouveau logo est tout de même adopté.}", "Ent", "19870300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "Microsoft", "", "USA", "Microsoft acquiert, en juillet, Forethought Inc., qui produit un logiciel de présentation nommé PowerPoint, qui initialement conçu pour le Macintosh d'Apple.}", "Ent", "19870700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1987", "Microsoft", "", "", "Microsoft commercialise Excel, en octobre, pour Windows. Il s’agit du premier produit tournant exclusivement sous Windows. Microsoft vendait déjà un autre tableur : Multiplan.}", "SW", "19871000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "NeXT", "", "USA", "NeXT dévoile son premier ordinateur.}", "Ent", "19880000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "", "", "", "Unisys rachète Convergent Technologies qui avait un système bureautique élaboré. Comme Bull avait passé des accords avec Convergent Technologies, cet OS est vendu chez Bull sous le nom de Questar et chez Unisys sous le nom de BTOS.}", "", "19880000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "", "", "", "Sun adopte l'architecture RISC (microprocesseur à jeu d'instructions réduit) pour sa série de station de travail. Cette architecture va se rependre pendant 10 ans.}", "", "19880000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "Pixar", "", "USA", "Tin Toy de Pixar est le premier film animé par ordinateur à recevoir un Academy Award (Best Animated Short film)}", "H", "19880000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "3D System", "", "USA", "Lancement de la SLA-250, la première imprimante 3D par 3D System.}", "HW", "19880000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "", "", "", "EARN et Eunet (European UNIX Network) lancent le domaine .be, en juin. Le professeur Pierre Verbaeten de la KULeuven en a la gestion.}", "", "19880600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1988", "", "Protocole IRC", "", "Création d'IRC (Internet Relay Chat) un protocole de communication textuelle sur Internet, en août 1988. Il sert à la communication instantanée (chat).}", "SW", "19880800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "Bull", "", "", "Le groupe Bull acquiert Zenith Data Corporation (USA), dont les produits phares sont les PC portables.}", "Ent", "19890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "", "", "Suisse", "Début de la conception du Web au CERN (Organisation européenne pour la recherche nucléaire) par Timothy Berners-Lee (1955-) et le belge Robert Cailliau (1947-).}", "H", "19890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "Apple", "", "USA", "Lancement du Macintosh Portable. EXPOT : I&B}", "HW", "19890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "", "", "", "Premier virus informatique créant de gros dégâts : le Dark Avenger, il est apparu au printemps en Bulgarie.}", "", "19890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "", "", "", "Création de l'HTML (Hypertext Markup Language) par Tim Berners-Lee sur base du SGML. C’est un langage de balisage qui permet d’écrire de l’hypertexte, de structurer sémantiquement le contenu des pages, d’y inclure des ressources multimédias.}", "Lang prog", "19890000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "Nintendo", "Gameboy", "Japon", "Nintendo sort, en avril, la Gameboy : une console portable de jeux vidéo 8-bits de quatrième génération.}", "HW", "19890400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1989", "", "Chute du mur de Berlin", "De", "Chute du Mur de Berlin (De), le 9 novembre.}", "H", "19891109");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1990", "IBM", "", "USA", "Fin de la vente de machines à écrire chez IBM (International Business Machines).}", "HW", "19900000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1990", "Adobe", "", "USA", "Adobe Photoshop 0.1 voit le jour sur Macintosh. Photoshop sera la référence en matière de logiciel de traitement d'images. Il avait été créé par les frères Thomas et John Knoll sous le nom de « Display ».}", "SW", "19900000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1990", "Microsoft OS", "", "", "Microsoft lance Windows 3.0, le 22 mai 1990, et c’est le succès grâce à l'apparition des icônes : 10 millions d’exemplaires vendus en 2 ans. Il était fourni avec quelques applications comme le Bloc-notes, la calculatrice, Paintbrush (l'ancêtre de Paint), les jeux Reversi et Solitaire.}", "Ent", "19900522");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1990", "Bull", "", "", "Bull lance la gamme UNIX DPX/2 en octobre 1990.}", "HW", "19901000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1990", "", "", "Suisse", "Création du World Wide Web et de l'HTTP par Tim Berners-Lee et Robert Cailliau (un belge) au CERN}", "SW", "19909000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "", "", "UK", "Construction de La machine à différence du mathématicien anglais Charles Babbage, par le musée des Sciences de Londres (UK).}", "Ca", "19910000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "Apple/IBM", "", "USA", "Apple et IBM (International Business Machines) signent ensemble un accord de développement.}", "Ent", "19910000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "", "", "", "Diffusion en juin, de MS-DOS 5.0, version majeur de MS-DOS.}", "", "19910000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "", "", "BE", "Test du vote électronique en Belgique dans deux communes (Verlaine et Waarschot). Le système est mis au point pas les filiales belge de Bull et de Phillips.}", "SW", "19910000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "", "", "", "Le premier site web est créé au CERN, le 1er août 1991. Il y aura cette année-là, 10 sites web consultables.}", "SoI", "19910801");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1991", "", "", "Fi", "Le finlandais, Linus Torvald diffuse le noyau du futur LINUX, le 5 octobre 1991.}", "SW", "19911005");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1992", "", "", "", "La société allemande SAP met sur le marché la version SAP-3 de sa solution complète ERP (entreprise ressource planning) se base sur une base de données Oracle. A partir de l'an 200, SAP deviendra la référence dans son domaine.}", "", "19920000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1992", "", "", "BE", "Création de Numerica , spin-off de la VUB (Vrije Universiteit Brussel) spécialisée dans les systèmes d’informations géographique (SIG). Deviendra vite la première européenne dans son domaine.}", "Ent", "19920000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1992", "Belgacom", "", "Be", "La RTT se transforme en société anonyme de droit public sous le nom de Belgacom.}", "Ent", "19920000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1992", "IBM", "", "", "IBM Simon (le premier « smartphone ») est inventé. Il dispose d'un service de messagerie, peut recevoir des fax et servir de PDA (assistant numérique personnel ou agenda électronique). Il est par ailleurs équipé d'un traitement de texte rudimentaire et d'un écran tactile. Il sera commercialisé à partir de 1994.}", "HW", "19920000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1992", "IBM", "", "", "IBM (International Business Machines) lance la gamme d'ordinateurs portables « ThinkPad », en octobre 1992, dont les 3 premiers modèles sont : 700, 700C et 700T. EXPOT : cf I&B}", "HW", "19921000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "IBM", "", "USA", "IBM (International Business Machines) enregistre la plus grosse perte de son histoire : $8 milliards.}", "Ent", "19930000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "", "", "USA", "Bill Clinton devient président des USA. Son vice-président Al Gore poussera l'idée des autoroutes de l'information.}", "H", "19930000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "", "", "BE", "Albert II de Belgique devient le sixième roi des Belges.}", "H", "19930000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "", "", "", "Première Webcam connectée sur Internet, au département des sciences informatiques de l'université de Cambridge (UK). Elle a été initialement branchée en 1991 par James Quentin Stafford-Fraser et Paul Jardetzky afin de surveiller le niveau de la cafetière et éviter ainsi de se déplacer pour rien. Elle est coupée le 22 août 2001.}", "IO", "19930000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "NCSA/CERN", "", "USA", "Création, au CERN, par Marc Andreesen, du navigateur Web Mosaïc (ancêtre de Netscape, Mozilla,...) : un logiciel conçu pour consulter et afficher le World Wide Web.}", "SW", "19930000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "Microsoft", "", "", "Commercialisation, en mars, par Microsoft, de Encarta, la première encyclopédie destinée à un ordinateur et vendue sur CD-ROM.}", "SW", "19930300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "Microsoft", "", "", "En avril, plus de 25 millions de personnes utilisent Windows à travers le monde.}", "Ent", "19930400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "CERN", "", "", "Le CERN (Organisation européenne pour la recherche nucléaire) autorise une utilisation publique du World Wide Web, le 30 avril.}", "SoI", "19930430");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "Microsoft", "", "", "Commercialisation, en juillet, du premier OS Windows NT (New Technology), il ne repose plus sur MS-DOS. Microsoft s’attaque au monde des serveurs d’entreprise.}", "Ent", "19930700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "", "", "", "Sortie, le 19 juillet, de Slackware la première distribution de Linux.}", "", "19930719");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1993", "Apple", "PDA Newton", "USA", "Le Newton est un assistant personnel numérique mis sur le marché, le 2 août, par Apple (abandonné en 1998). Il préfigure la tablette tactile.}", "HW", "19930802");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "", "", "Premier écran plat pour PC en technologie LCD (affichage à cristaux liquides) ceux-ci sont encore en noir et blanc.}", "", "19940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "Adobe", "", "USA", "Adobe acquiert Aldus (avec le logiciel Pagemaker).}", "Ent", "19940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "", "BE", "Création de la licence en informatique à horaire décalé FUNDP ?}", "H", "19940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "AltaVista", "USA", "Lancement du moteur de recherche AltaVista, une application Web permettant de trouver des ressources à partir d'une requête sous forme de mots.}", "SW", "19940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "", "USA", "Réalisation du navigateur Netscape par Netscape Communication Corporation fondé par Marc Andreesen.}", "SW", "19940000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "Fondation de Yahoo!", "", "En janvier, deux étudiants de Stanford (David Filo et Jerry Yang, USA) créent une société de service en ligne : Yahoo!}", "", "19940100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "Proximus", "", "BE", "Fondation de Proximus, le premier janvier, comme coentreprise de Belgacom (75%) et Airtouch (25%), pour exploiter un réseau de GSM (Global System for Mobile) sur la Belgique.}", "Ent", "19940101");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "", "FR/UK", "Inauguration officielle du tunnel sous la Manche (Fr/UK) par la reine Elizabeth II et le président François Mitterrand, le 6 mai.}", "H", "19940506");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "", "", "Première application de vote électronique à grande échelle, aux élections européennes, pour 20 % de la population belge. Il y aura des bugs.}", "", "19940612");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "", "Fondation d’Amazon", "", "Fondation, par Jeff Bezos, en juillet, d'Amazon.com Inc. une entreprise de commerce électronique américaine basée à Seattle (USA), le site internet du même nom sortira en 1995. Amazone.com deviendra un des leaders du commerce en ligne.}", "", "19940700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1994", "Netscape", "", "USA", "Le 13 octobre, la version 0.9 beta de Mosaïc Netscape intègre pour la première fois, la technologie des cookies pour déterminer si les visiteurs du site web Netscape avaient déjà visité le site auparavant. Lou Montulli déposera un brevet sur les cookies en 1995.}", "SW", "19941013");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "", "Présentation officielle au SunWorld (San Francisco, USA), le 23 mai, du langage Java : langage de programmation informatique orienté objet créé par James Gosling et Patrick Naughton, employés de Sun Microsystems, avec le soutien de Bill Joy (cofondateur de Sun Microsystems en 1982).}", "", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "IBM", "", "USA", "IBM (International Business Machines) rachète Lotus Software pour 3,5 milliards de dollars.}", "Ent", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Pixar", "", "USA", "Sortie du film d'animation Toy Story des studios Pixar.}", "Ent", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "USA", "Sortie du film Hackers de Iain Softley.}", "H", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "", "Le W3C (WWW Consortium) devient opérationnel. C'est un organisme de normalisation à but non lucratif, chargé de promouvoir la compatibilité des technologies du World Wide Web telles que HTML, XHTML, XML, RDF, SPARQL, CSS, XSL, PNG, SVG et SOAP.}", "SoI", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "", "Il y aura cette année-là 19.000 sites Web consultables.}", "SoI", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "eBay", "", "USA", "Début de la société eBay (Echo Bay Technology), connue par son site web de ventes aux enchères du même nom. Elle a été créée par Pierre Omidyar (USA). Elle est devenue une référence mondiale dans son secteur et un phénomène de société. En 2009, elle comptait plus de 276 millions de membres inscrits.}", "SW", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Unisys", "", "", "Unisys introduit « ClearPath Heterogeneous Multi-Processing » (HMP), qui permet d'intégrer une série A et la série 2200 (applications et bases de données) avec UnixWare et Windows NT (applications et bases de données) sur une plate-forme unique. Expot : ClearPath}", "SW", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "SSD", "", "Commercialisation du premier « Solid State Drive » (SSD), le FFD-350.}", "", "19950000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "Fr", "Lancement en janvier du premier fournisseur d'accès à Internet en Belgique : Skynet. Il sera racheté par Belgacom en 1998.}", "Ent", "19950100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Banksys", "", "BE", "Banksys lance, en février, le porte-monnaie électronique en test dans deux villes belges : Leuven et Wavre, nommé \"Proton\", basé sur la carte à puce. Expot : carte proton Bull}", "SW", "19950200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Microsoft Logiciel", "", "USA", "Microsoft et DreamWorks SKG (Spielberg, Katzenberg et Geffen) fondent DreamWorks Interactive, en mars.}", "Ent", "19950300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "Be", "Le portail en ligne officiel de la Belgique (www.belgium.be) est inauguré, en avril. Il y a, à cette époque, 100.000 belges abonnés à Internet.}", "SoI", "19950400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "", "", "", "Libéralisation d'Internet (ouverture au public), le 30 avril.}", "SoI", "19950430");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Microsoft", "", "", "Microsoft s’engage dans Internet, en août, en lançant le navigateur Microsoft Internet Explorer. Il sera abandonné en 2015 pour Microsoft Edge sous Windows 10.}", "Ent", "19950800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1995", "Microsoft", "", "", "Lancement de Windows 95 en août. Un an après sa sortie, il deviendra le système d'exploitation le plus populaire jamais conçu.}", "OS", "19950800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1996", "IBM", "", "USA", "IBM (International Business Machines) rachète Tivoli Systems pour un montant de 750 millions $.}", "Ent", "19960000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1996", "Apple", "", "USA", "NeXT est racheté pour 429 millions de dollars par Apple, qui recherchait un nouveau système d’exploitation pour ses ordinateurs Macintosh. À la suite de ce rachat, Steve Jobs se retrouve propulsé à la tête d’Apple.}", "Ent", "19960000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1996", "Microsoft", "", "USA", "Les américains Jack Smith et Sabeer Bhatia lancent un service de courrier électronique gratuit sur le Web baptisé HoTMaiL, le 4 juillet.}", "Ent", "19960704");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1996", "Microsoft Logiciel", "", "", "Lancement d’Internet Explorer 3.0, en août, c’est le début de la fin pour Netscape.}", "SW", "19960800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1996", "CERN", "", "", "Publication de la spécification CSS1 (Cascading Style Sheets, feuilles de style en cascade), le 17 décembre, créée par Håkon W. Lie. Elles forment un langage informatique qui décrit la présentation des documents HTML et XML}", "Lang prog", "19961217");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "IBM", "", "USA", "Le Deep Blue (un super-calculateur spécialisé dans le jeu d'échec) bat Garry Kasparov (le champion du monde d'échecs de l'époque) lors du match revanche (premier match remporté par Garry Kasparov en 1996).}", "HW", "19970000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "NEC/Zenith", "", "", "Accord de fusion entre Zenith Data System (Bull), NEC et Packard-Bell.}", "Ent", "19970000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "Google", "Fondation de Google", "USA", "Larry Page et Sergueï Brin (USA) décident de renommer le programme BackRub (créé à partir de 1996 en Google, mot dérivé du terme anglais \"googol\" désignant le chiffre 1 suivi de 100 zéros.}", "Ent", "19970000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "Sony/Philips", "DVD", "", "Sony et Philips s'associe pour lancer le DVD.}", "Ent", "19970000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "", "", "USA", "Le différend qui opposait Microsoft et Apple au sujet de la paternité (et de la propriété) de l’interface graphique prend fin, en août, à l’amiable et Microsoft entre dans le capital de Apple à hauteur de 150 millions de dollars.}", "Ent", "19970800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1997", "Microsoft", "", "USA", "La société Hotmail est acquise, le 30 décembre, pour $400 millions par Microsoft qui l’intègre à sa palette de service en la rebaptisant MSN Hotmail.}", "Ent", "19971230");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "", "", "", "NCR qui appartient au groupe ITT s'oriente définitivement bers les services informatiques (et abandonne complètement la création d'ordinateurs).}", "", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Compaq/DEC", "", "USA", "Compaq achète Digital Equipment Corp (DEC). Les produits de DEC vont disparaître petit à petit.}", "Ent", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Microsoft", "", "USA", "Le département américain de la Justice et les procureurs généraux d'une vingtaine d’États ont déposé une plainte anti-trust contre Microsoft.}", "Ent", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Nokia", "", "", "Nokia devient le premier constructeur mondial de téléphones mobiles.}", "Ent", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "", "", "USA", "American Online achète Netscape pour $4,2 milliards.}", "Ent", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "PayPal", "PayPal", "USA", "Lancement de PayPal (USA) un système de paiement par Internet, par Max Levchin, Peter Thiel, Luke Nosek et Ken Howery.}", "Ent", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Apple", "iMac", "USA", "Apple lance le iMac, une gamme d’ordinateurs tout-en-un grand public. EXPOT :iMac}", "HW", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "", "", "", "Création de l’Open Source Initiative, une organisation voué à la promotion de logiciels libres (open source), logiciel dont l’accès au code source est sous licence GNU (GNU’s Not UNIX) par Todd Anderson, Chris Peterson, John \"maddog\" Hall, Larry Augustin, Sam Ockman et Michael Tiemann sous l'impulsion de Bruce Perens et Eric S. Raymond.}", "SoI", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "IBM", "", "", "Lou Gerstner, directeur d'IBM (International Business Machines) dit L' ère du PC est terminée, les clients n’achètent plus de PC et les PC ne seront plus la principale plate-forme pour le développement d'applications. (« The PC era is over, in terms of PCs driving customer buying decisions and begin the primary platform for application development ».)}", "SoI", "19980000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Microsoft", "", "USA", "Début du procès « anti-trust » contre Microsoft aux États-Unis, en mai.}", "Ent", "19980500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1998", "Google", "", "USA", "Les statuts californiens (USA) de la société Google sont déposés, le 4 septembre. Peu après, Larry Page et Sergueï Brin ouvrent un compte bancaire au nom de leur toute nouvelle société, et y déposent le chèque de 100.000$ d’Andy Bechtolsheim (cofondateur de Sun).}", "Ent", "19980904");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "Shazam Ent Ld", "Shazam", "UK", "Création du logiciel Shazam pour la reconnaissance musical par Shazam Entertainment Limited (par les anglais : Chris Barton, Philip Inghelbrecht, Avery Wang et Dhiraj Mukherjee).}", "Ent", "19990000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "", "The Matrix", "Autralie/USA", "Sortie du film australo-américain de science-fiction, The Matrix, réalisé par Larry et Andy Wachowski.}", "H", "19990000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "", "QR code", "", "Le système QR code inventé par Toyota en 1994 est standardisé par ISO.}", "", "19990000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "", "Arrêt d code Morse", "", "Fin de l'utilisation internationale du code Morse, le premier septembre.}", "SoI", "19990901");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "", "", "", "Découverte du premier virus informatique s'introduisant dans les ordinateurs via les e-mails.}", "", "19991109");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("1999", "", "", "", "Dernier préparatif pour échapper au « bug de l'an 2000 ». Grâce à la prise en charge du risque, il n'aura pas lieu.}", "", "19999999");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "", "Nokia 3310", "", "Nokia sort son téléphone mobile « 3310 ». Ce téléphone a été extrêmement populaire : environ 126 millions d'exemplaires ont été écoulés jusqu'à son retrait du marché, ce qui en fait un des téléphones mobiles les plus vendus au monde.}", "HW", "20000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "", "", "", "Il y aura cette année là 10.000.000 sites web consultables.}", "SoI", "20000000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "Microsoft", "", "USA", "Bill Gates cède sa place, en janvier, à Steve Ballmer à la tête de Microsoft.}", "Ent", "20000100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "Microsoft", "Windows 2000", "", "Commercialisation de Windows 2000, en février, connu également sous le nom de Windows NT 5.0 disponible pour serveur et station de travail, considéré comme une amélioration majeur des systèmes d'exploitation précédents.}", "OS", "20000200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "Google Yahoo !", "", "", "Google conclut un partenariat avec Yahoo! en juin, qui tiendra jusqu'en 2004. Google deviendra le service de recherche par défaut de Yahoo ! ce qui en fera le moteur de recherches le plus utilisé au monde.}", "Ent", "20000600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "Google", "", "", "Début de Google AdWords le 23 octobre. Les annonceurs paient à Google une rétribution quand l'internaute clique sur la publicité : plus l'annonce sera pertinente, plus le prix au clic sera bas et l'annonce mise en avant. Cela incite les publicitaires à fournir des renseignements pertinents par rapport à la demande de l'internaute.}", "SW", "20001023");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2000", "", "", "", "En octobre, John Titor commence à poster sur différents forums des messages traitant de voyages dans le temps en affirmant être un voyageur temporel en provenance de l'année 2036 ! Les publications s’arrêteront en mars 2001. Si ce personnage est certainement un bonne blague, elle a inspiré de nombreuses fictions (films, documentaires, romans, sites Internet,...).}", "", "20001000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "IBM", "", "USA", "IBM (International Business Machines) acquiert la division bases de données d'Informix Software pour un montant de $1 milliard.}", "Ent", "20010000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "", "", "USA", "George W. Bush devient président des USA.}", "H", "20010000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Apple", "", "", "Apple lance le Max OS X, un dérivé de NeXTSTEP.}", "OS", "20010000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "", "", "", "Sortie du film A.I. Intelligence artificielle réalisé par Steven Spielberg, adapté de la nouvelle de Brian Aldiss, Les Supertoys durent tout l'été.}", "", "20010000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Apple", "", "USA", "Sortie de la première version de iTune, un logiciel de lecture et de gestion de bibliothèque multimédia numérique distribué gratuitement par Apple.}", "SW", "20010000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "", "", "USA", "Lancement de Wikipedia par Wikipedia Foundation de Jimmy Wales, le 15 janvier. En 2015, Wikipédia sera classé le 7eme site le plus visité.}", "Ent", "20010115");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Apple", "", "USA", "Apple lance les premiers Apple Store à Glendale (Californie) et McLean (Virginie), le 19 mai.}", "Ent", "20010519");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "", "", "Be", "Sortie du film Thomas est amoureux de Pierre-Paul Renders, le 21 juin.}", "H", "20010621");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Microsoft", "", "", "Commercialisation, en août, de Windows XP, qui est la réunification de Windows 2000 pour sa robustesse et des Windows 9x pour l’aspect utilisateur individuel, avec une interface simple d’utilisation. Cette version ne sera plus supportée à partir d'avril 2015.}", "OS", "20010800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "", "", "USA", "Attentats, le 11 septembre 2001, visant les tours jumelles du World Trade Center à Manhattan (New-York) et le Pentagone, à Washington DC, siège du Département de la Défense. Ces attentats font 2.977 victimes.}", "H", "20010911");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Apple", "", "", "Apple lance l'iPod, en octobre, un baladeur numérique doté d'un disque dur de 5GB pouvant contenir, selon Apple, 1.000 chansons.}", "HW", "20011000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2001", "Microsoft", "", "USA", "Lancement par Microsoft, le 15 novembre, de la Xbox, aux États-Unis (le 14 mars 2002 en Europe). Elle est vendue à prix coûtant pour développer les ventes de jeux.}", "Ent", "20011115");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2002", "IBM", "", "", "IBM (International Business Machines) acquiert l’activité de conseil PwC consulting pour $3,5 milliards.}", "Ent", "20020000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2002", "", "", "BE", "Début de la carte d'identité électronique en Belgique.}", "HW", "20020000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2002", "Casio", "", "", "Casio est le premier à lancer, en avril, un téléphone cellulaire avec appareil photo intégré numérique (le A3012CA, également équipé d'un GPS).}", "HW", "20020400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "Texas Instruments", "", "USA", "Suite à un accord entre Texas Instruments et les États-Unis, Texas Instruments favorise la mise à disposition de la calculette TI 83 aux écoles. Expot : SYSCAL.68.1042}", "Ent", "20030000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "IBM", "", "", "IBM (International Business Machines) achète Rational pour $2,1 milliards.}", "Ent", "20030000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "LinkedIn", "", "USA", "Création de LinkedIn à Mountain View (Californie, USA), réseau social professionnel en ligne.}", "Ent", "20030000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "", "", "Su", "Création du site The Pirate Bay par les suédois Gottfrid Svartholm, Fredrik Neij, Peter Sunde, Alex Martins. Il deviendra l'un des plus grand sites web de référenceur de fichiers BitTorrent.}", "SW", "20030000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "", "", "USA", "Création, en août, de MySpace : un site web de réseautage social, fondé par Tom Anderson et Chris DeWolfe aux États-Unis.}", "Ent", "20030800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "", "", "USA", "Création de Skype, en août, un logiciel gratuit permettant aux utilisateurs de passer des appels téléphoniques ou vidéo via Internet, par les estoniens Niklas Zennström et Janus Friis.}", "Ent", "20030800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2003", "Google", "", "", "Google lance Google Print, en décembre, (rebaptisé Google Books en août 2006).}", "SW", "20031200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "", "", "", "Adoption du premier standard de reconnaissance à distance de puces électroniques : RFID, une méthode pour mémoriser et récupérer des données à distance en utilisant des marqueurs appelés « radio-étiquettes ».}", "HW", "20040000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "", "", "USA", "Aux États-Unis débute le succès mondial du portable de poche \"Blackberry\", le PDA qui permet le traitement des e-mails. L'Europe suit en 2005.}", "HW", "20040000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "Google", "", "", "Création par Google de Gmail qui n'est disponible que sur invitation, en janvier. Il sera ouvert à tout le monde à partir du 14/02/2007.}", "Ent", "20040100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "FB", "", "USA", "Lancement du réseau social Facebook, le 4 février, à l'université de Harvard (USA). En juin, Facebook sera disponible au sein de 30 campus et atteindra les 150.000 membres.}", "Ent", "20040204");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "", "", "", "En avril, le club Baja Beach Club de Barcelone (Es), propose de se faire poser un implant sous cutané afin d'entrer et de payer leurs consommations : c'est le début des « cyborgs ».}", "", "20040400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "TI", "", "USA", "Le premier mai, Rich Templeton devient le nouveau PDG de Texas Instruments.}", "Ent", "20040501");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "Google", "", "USA", "Google fait son entrée en bourse à Wall Street, en août, par l’offre de vente de 19.605.052 actions de classe A. À l’ouverture, le prix de l’action est de $85.}", "Ent", "20040800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "Blizzard Entertainment", "", "USA", "Sortie de World of Warcraft (WoW), en novembre, un jeu vidéo de type MMORPG (massively multiplayer online role-playing game ou jeu de rôle en ligne massivement multijoueur).}", "SW", "20041100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "", "", "", "Un tsunami a frappé le 26 décembre, l'Indonésie, les côtes du Sri Lanka, le sud de l'Inde et l'ouest de la Thaïlande, bilan : 227.898 morts.}", "H", "20041226");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2004", "FB", "", "USA", "Facebook regroupe 1 million de membres au 30 décembre.}", "Ent", "20041230");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "IBM", "", "Chine", "IBM (International Business Machines) vend sa division de fabrication d'ordinateurs personnels à Lenovo (Chine) et acquiert l’entreprise SPSS Inc. pour $2 milliard et Ascential Software Corporation.}", "Ent", "20050000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Megaupload", "", "USA", "Création de Megaupload par Kim Dotcom (USA), site web qui permettait à un internaute de mettre en ligne des fichiers dans la limite de 1 Go pour les utilisateurs libres, et sans limite pour les utilisateurs Premium.}", "Ent", "20050000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "", "", "", "Il y aura cette année-là 74.000.000 sites web consultables.}", "SoI", "20050000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "YouTube", "", "USA", "Lancement de YouTube, en février, par Steve Chen, Chad Hurley et Jawed Karim (trois anciens employés de PayPal), un site web d’hébergement de vidéos sur lequel les utilisateurs peuvent envoyer, regarder et partager des vidéos. YouTube sera racheté par Google en octobre 2006 pour la somme de $1,54 milliard.}", "SW", "20050200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Google", "", "USA", "Google Maps, un service gratuit de cartographie en ligne, voit le jour en février.}", "SW", "20050200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Telenet/Belgacom", "", "BE", "Lancement en Belgique de la télévision numérique par câble et de la télévision numérique via ligne ADSL (Telenet, Belgacom), en septembre.}", "HW", "20050700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Google", "", "", "En août, Google rachète Android, petite société ayant développé un système d’exploitation pour les smartphones et lance Google Talk, une application permettant de chatter via des messages instantanés ou à l’aide d’un microphone et d’un haut-parleur d’ordinateur (sans besoin d’un téléphone).}", "Ent", "20050800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Google", "", "USA", "Vint Cerf (inventeur du protocole TCP/IP) quitte l’agence DARPA (« Agence pour les projets de recherche avancée de défense »), aux USA, pour rejoindre Google, en septembre.}", "Ent", "20050900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2005", "Adobe", "", "USA", "Adobe acquiert Macromedia société qui a créé Dreamweaver et Flash, le 3 décembre.}", "Ent", "20051203");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "Google", "", "", "Google lance Picasa Albums Web, en juin, qui permet d’importer et de partager des photos en ligne.}", "SW", "20060600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "", "", "", "Le support optique Blu-ray est commercialisé.}", "", "20060600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "Nokia Siemens", "", "", "Nokia et Siemens annoncent la fusion de leurs activités de réseaux de télécommunication, le 18 juin, donnant ainsi naissance à un géant mondial : Nokia Siemens Networks.}", "Ent", "20060618");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "Twitter", "", "", "Lancement, en juillet, de Twitter, une plate-forme de microblogging, créée par Jack Dorsey, Evan Williams, Biz Stone et Noah Glass.}", "Ent", "20060700");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "", "", "", "AMD (l'un des plus grands constructeurs de processeurs) rachète ATI (l'un des plus grands constructeurs de processeurs graphiques).}", "", "20060725");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "FB", "", "USA", "Ouverture de Facebook au public (pour les plus de 13 ans uniquement), le 6 septembre. Facebook n'est donc plus réservé aux universitaires.}", "Ent", "20060906");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "Nintendo", "", "Japon", "Lancement de la Nintendo Wii, dont le système est capable de détecter la position, l'orientation et les mouvements dans l'espace de la manette, par Nintendo, en octobre.}", "SoI", "20061000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "Microsoft", "", "", "Microsoft introduit Windows Vista, pour les entreprises le 30 novembre 2006. Les particuliers, eux, devront attendre le 30 janvier 2007.}", "Ent", "20061130");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2006", "", "", "Islande", "Lancement du site Internet WikiLeaks, en décembre, un site web lanceur d'alerte, qui rend public des documents protégés par les États.}", "SoI", "20061200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2007", "", "", "", "Début de la crise financière mondiale des subprimes (due aux crédits immobiliers excessifs).}", "H", "20070000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2007", "Apple", "", "", "Apple lance, en mars, l'Apple TV.}", "Ent", "20070300");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2007", "Google", "", "USA", "Google Street View est disponible, en mai, dans Google Maps pour cinq villes américaines : New York, San Francisco, Las Vegas, Miami et Denver.}", "SW", "20070500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2007", "Apple", "", "", "Apple sort l'iPhone, son premier smartphone, le 29 juin.}", "HW", "20070629");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2007", "Amazon.com", "", "USA", "Lancement du Kindle, une liseuse commercialisée par Amazon.com, le 19 décembre.}", "HW", "20071219");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "Unisys", "", "Chine", "Unisys aide l'aéroport de Pékin (Chine) à se préparer pour les Jeux olympiques de 2008.}", "Ent", "20080000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "IBM", "", "", "IBM (International Business Machines) achète Cognos pour $5 milliards et Diligent Technologies (stockage et dé-duplication).}", "Ent", "20080000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "", "", "Be", "Sortie du film Ben X réalisé par le belge Nic Balthazar.}", "H", "20080000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "", "", "", "Packard Bell est racheté par Acer.}", "", "20080000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "", "", "", "Première manifestation médiatisée des Anonymous (un mouvement « hacktiviste ») avec le projet Chanology (une série d'attaques informatiques visant l'église de scientologie).}", "SoI", "20080000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "Google", "", "", "Google migre de IPv4 (Internet Protocol version 4) à l'Ipv6 afin de pallier au manque d'adresses Ipv4, en avril.}", "SW", "20080400");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2008", "Google", "", "", "Google lance Chrome en septembre, son navigateur.}", "SW", "20080900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "Uber", "", "USA", "Création de Uber, une application de mise en contact d'utilisateurs avec des conducteurs réalisant des services de transport, fondée à San Francisco (USA) par Garrett Camp, Travis Kalanick et Oscar Salazar.}", "Ent", "20090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "", "", "USA", "Barack Obama devient président des USA.}", "H", "20090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "Google", "", "", "Google annonce sa voiture « self-driving » (« Google Car »), un projet de voiture autonome.}", "HW", "20090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "", "", "", "La monnaie Bitcoin est inventée. Le nom désigne à la fois un système de paiement à travers le réseau Internet et l'unité de compte utilisée par ce système de paiement.}", "SoI", "20090000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "Microsoft", "", "", "Yahoo! et Microsoft signent un accord pour que Bing remplace le moteur de recherche de Yahoo!, le premier juin.}", "SW", "20090601");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2009", "", "", "Fr", "La loi qui favorise la diffusion et la protection de la création sur internet, dite loi Hadopi, est adoptée en France le 12 juin. Elle protège les droits d'auteurs sur des créations qu'on transmet par Internet.}", "C", "20090612");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "IBM", "", "USA", "IBM (International Business Machines) achète Sterling Commerce à AT&T (American Telephone & Telegraph) pour $1.4 milliard, BigFix, OpenPages, Unica et Netezza.}", "Ent", "20100000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "", "", "USA", "Sortie du film The Social Network réalisé par David Fincher retraçant la création de Facebook par Mark Zuckerberg.}", "H", "20100000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "", "", "", "Il y aura, en février, 233.636.281 sites web consultables.}", "SoI", "20100200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "Apple", "", "", "Apple commercialise l'iPad, le 3 avril.}", "HW", "20100403");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "HP", "", "", "HP rachète la société Palm pour $1,2 milliard afin de créer ses premiers smartphones sous le label HP, le 28 avril. expo}", "Ent", "20100428");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "", "", "BE", "Google ouvre un DataCenter à Saint-Ghislain (Mons) en septembre.}", "Ent", "20100900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "Bull", "", "", "Bull a franchi, avec son supercalculateur TERA 100 la barre du Pétaflop, en novembre.}", "HW", "20101100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "", "", "", "Microsoft commercialise le 10 novembre en Europe, un capteur de mouvements Kinect, destiné à sa console de jeux Xbox 360.}", "", "20101110");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2010", "", "", "", "Début des « Printemps arabes » avec l'immolation de Mohamed Bouazizi à Sidi Bouzid (Tunisie), le 17 décembre, déclenchant des manifestations massives et répétées qui utiliseront massivement les réseaux sociaux.}", "H", "20101217");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Microsoft", "", "", "Rachat de Skype, un logiciel permettant aux utilisateurs de passer des appels téléphoniques via Internet, pour une somme de $8,5 milliards par Microsoft.}", "Ent", "20110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "Inde", "Fermeture de la dernière fabrique à machines à écrire, celle de Godrej & Boyce à Mumbai en Inde.}", "Ent", "20110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Google", "", "", "Lancement du projet Google Glass par le Google X Lab.}", "HW", "20110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "", "Premier KIKK festival (Namur), les 24 et 25 novembre.}", "", "20110000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Google", "", "", "Lancement du projet Google Art, le premier février, qui permet de visiter virtuellement 17 des plus grands musées du monde entier et de découvrir des images haute résolution de plus de 1.000 œuvres d’art. D'autres musées s'ajouteront par la suite.}", "C", "20110201");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "Japon", "Accident nucléaire à Fukushima (Japon), le 11 mars.}", "H", "20110311");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "", "Nintendo commercialise, le 25 mars en Europe, la Nintendo 3DS : première console portable à écran auto-stéréoscopique.}", "", "20110325");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "Pakistan", "Oussama ben Laden, chef spirituel du réseau jihadiste Al-Qaïda est tué lors d'une opération militaire américaine, le 2 avril.}", "H", "20110502");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Google", "", "", "Google lance, en juin, le projet Google+, une application de réseau social.}", "Ent", "20110600");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Google", "", "", "Le groupe Google a racheté Motorola et tous ses brevets en téléphonie portable pour $12 milliards, en août.}", "Ent", "20110800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "HP", "", "USA", "HP annonce, en août, la fin de sa production de PC pour la fin de l'année.}", "Ent", "20110800");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Google", "", "", "Google, en partenariat avec le musée d’Israël, numérise les anciens manuscrits de la mer Morte, en septembre.}", "Ent", "20110900");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "Thaïlande", "À cause d'inondations en Thaïlande, en octobre, en particulier dans la région de Bangkok, il y aura 3 mois de pénurie mondiale des stocks de disques durs pour PC et la première hausse des prix dans ce genre de produit, la majorité de la production venant de là.}", "H", "20111000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "Apple", "", "USA", "Décès de l'américain Steve Jobs, figure clé de Apple, le 5 octobre.}", "Ent", "20111005");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "Japon", "Le superordinateur japonais \"K\" (Kioto), créé par le groupe informatique Fujitsu et l'institut de recherche Riken passe le premier la barre des 10 pétaflops, en novembre. Il est destiné à diverses applications scientifiques au Japon.}", "HW", "20111100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2011", "", "", "UK", "Début de la diffusion, le 4 décembre, de la série télévisée Black Mirror créée par Charlie Brooker et produite par la société de production Zeppotron.}", "H", "20111204");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "Megaupload", "", "USA", "Fermeture, dans le cadre d'une campagne anti-piratage, par la justice américaine de Megaupload (Kim Dotcom), le 19 janvier.}", "Ent", "20120109");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "", "", "", "Début de la diffusion, le 22 janvier, de la série Real Humans : 100% humain créée par Lars Lundström, réalisée par Harald Hamrell et Levan Akin.}", "", "20120122");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "fondation Raspberry Pi", "", "UK", "Sortie du Raspberry Pi, le 29 avril, un nano-ordinateur monocarte à processeur ARM, par la fondation Raspberry Pi créée par David Braden à l’Université de Cambridge (UK). EXPOT : Raspberry Pi}", "Ent", "20120529");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "", "", "Be", "World Science Festival (New-York, USA) du 8 juin, accrédite la thèse selon laquelle l’idée d’un accès universel aux connaissances (l'idée d'Internet) est bien d’origine belge avec Paul Otlet.}", "H", "20120608");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "Microsoft", "", "", "Commercialisation par Microsoft de Windows 8, le 26 octobre. Cette version sera un échec : Gabe Newell (fondateur de Valve Corporation), ira jusqu’à dire : Windows 8 est une catastrophe pour le monde du PC.}", "SW", "20121026");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2012", "FB", "", "", "Le 31 décembre, 975.943.960 personnes étaient inscrites sur Facebook.}", "Ent", "20121231");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2013", "", "", "BE", "Philippe de Belgique devient le septième roi des Belges.}", "H", "20130000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2013", "", "", "USA", "Décès de l'américain Douglas Engelbart, le 2 juillet, pionnier de l’informatique qui a inventé la souris, célèbre pour ses travaux sur le développement de l'interface homme-machine.}", "H", "20130702");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2013", "", "", "USA", "Sortie du film Jobs réalisé par Joshua Michael Stern retraçant la carrière de Steve Jobs, le 21 août.}", "H", "20130821");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2013", "Microsoft", "", "", "Microsoft annonce le rachat de l'activité téléphonie mobile de Nokia pour 5,44 milliards d'€, le 2 septembre.}", "Ent", "20130902");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2013", "Bull", "", "", "Le groupe Bull annonce, en octobre, la fabrication de son smartphone Hoox, qui se veut le premier totalement sécurisé.}", "HW", "20131000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Proximus", "", "BE", "Belgacom annonce avoir choisi la marque Proximus pour tous ses produits et services.}", "Ent", "20140000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "", "", "UK/USA", "Sortie de Transcendance un film de science-fiction américano-britannique réalisé par Wally Pfister.}", "H", "20140000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "", "", "USA/UK", "Sortie du film The Imitation Game retraçant le passage d'Alan Turing à Bletchley Park réalisé par Morten Tyldum.}", "H", "20140000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Bull", "", "Fr", "ATOS acquiert Bull.}", "Ent", "20140000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "IBM", "", "", "IBM (International Business Machines) vend une partie de sa branche serveur à Lenovo pour $2,3 milliards, en janvier.}", "Ent", "20140100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Google", "", "FR", "En France, la Commission nationale de l'informatique et des libertés (CNIL) sanctionne Google, le 8 janvier, à une amende de 150.000 €, estimant que ses règles de confidentialité (depuis le 1er mars 2012) ne sont pas conformes à la Loi informatique et libertés.}", "Ent", "20140108");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Facebook", "", "", "Facebook rachète WhatsApp, en février, système de messagerie entre smartphones.}", "Ent", "20140200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Stésud", "", "Be", "Élection en Belgique du 24 avril, bug sur le vote électronique où 2250 voix seront perdues.}", "SW", "20140425");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Google", "", "", "Google met en place un formulaire de droit à l'oubli, le 30 mai, qui permet à l'internaute qui le souhaite de faire effacer certaines informations le concernant. En 24 h, Google avait déjà reçu 12.000 requêtes.}", "Ent", "20140530");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "", "", "", "Pour la première fois, un logiciel (« Eugène Goostman ») créé par les russes Vladimir Veselov et Eugene Demchenko, a réussi le test de Turing, le 7 juin, il a pu se faire passer pour un être humain.}", "SW", "20140607");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "", "", "Be", "Le premier août, la Belgique met en place la version informatisée du dossier médical global, le DMI (Dossier Médical Informatisé).}", "SW", "20140801");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "", "", "USA", "Ethan Zuckerman s’excuse d'avoir écrit le code des fenêtre « pop-up » permettant une publicité plus large dans son article « The Internet's Original Sin » sur Theatlantic.com, le 14 août.}", "SW", "20140814");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Microsoft / Mojang", "", "", "Microsoft annonce l'acquisition de Mojang, l'éditeur du jeu-vidéo Minecraft pour $2,5 milliards, le 15 septembre.}", "Ent", "20140915");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Apple", "", "", "Apple sort l'iPhone 6 et 6 Plus, le 19 septembre.}", "HW", "20140919");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Atos /Bull", "", "USA", "ATOS annonce le rachat de la filiale informatique de Rank Xerox aux États-Unis, en décembre.}", "Ent", "20141200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Pirate Bay", "", "Su", "Fermeture du site The Pirate Bay, en décembre, à la suite d'une descente de la police suédoise et de la confiscation des serveurs du site.}", "Ent", "20141200");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2014", "Uber", "", "Es", "Le service d'Uber, UberPOP (conducteurs particuliers occasionnels au volant de leur véhicule personnel), est interdit en Espagne, le 9 décembre, il sera interdit en France à partir du 3 juillet 2015.}", "Ent", "20141209");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "FB", "", "Monde", "Il y a 1,393 milliard d'utilisateurs actifs mensuels de Facebook, en janvier.}", "Ent", "20150100");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "Fr", "Attentat, le 7 janvier, contre le journal satirique « Charlie Hebdo » à Paris faisant 12 victimes revendiqué par Al-Qaïda.}", "H", "20150107");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "", "Sortie du film L’enquête, le 11 février, réalisé par Vincent Garenq, retraçant l'affaire Clearstream, on y voit des machines Bull.}", "H", "20150211");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "Uber", "", "Sui", "Le Canton de Genève (Suisse) déclare le service UberX (chauffeurs professionnels proposant des berlines standards) opéré sur Genève illégal, le 13 avril.}", "Ent", "20150413");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "TEC", "", "BE", "Les TEC (Trabsport en Commun) belges lancent la carte à puce sans contact « Mobib/TEC », le 21 avril.}", "H", "20150421");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "Népal", "Tremblement de terre, le 25 avril, de magnitude 7.8 au Népal, plus de 8.046 morts et plus de 17.000 blessés.}", "H", "20150425");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "", "Le film Citizenfour sur les révélations de Edward Snowden (scandale d’espionnage de la NSA (National Security Agency)), reçoit le palme du meilleur documentaire à Cannes, en mai.}", "H", "20150500");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "Uber", "", "Be", "Un chauffeur Uber belge a été reconnu coupable, le 4 mai, devant le tribunal de police de Bruxelles, d’infraction à la réglementation relative au transport rémunéré de personnes.}", "Ent", "20150504");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "Nintendo", "", "Japon", "Le PDG du groupe de jeux vidéo Nintendo, Satoru Iwata, meurt d'un cancer à l’âge de 55 ans, le 11 juillet.}", "Ent", "20150711");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "Google", "", "USA", "Google annonce un changement majeur dans la structure de l'entreprise afin de devenir Alphabet Inc. regroupant divers entreprises, le 10 août.}", "Ent", "20150810");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "", "En octobre, le champion européen du jeu de go a été battu par le programme de Google DeepMind baptisé AlphaGo. Lee Sedol, n° 3 mondial, s'y mesurera en mars 2016 et sera battu (4-1).}", "", "20151000");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "Fr", "Attentats du 13 novembre 2015 en France (130 morts et 351 blessés).}", "H", "20151113");
INSERT INTO GENERAL (Annee, Société, Titre, Localisation, Description, Type, Full_Date) VALUES ("2015", "", "", "", "Sortie du film Star Wars Episode VII: The Force Awakens, 16 décembre, réalisé par J. J. Abrams.}", "H", "20151216");